import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { Auth } from "aws-amplify";
import { withRouter } from 'react-router-dom';
import * as Yup from 'yup';
import { withFormik, Form } from 'formik';

import FormField from "../FormField";

import logoPlaystars from './../img/logo_final.svg';
import './styles.scss';

FormField.propTypes = {
    id: PropTypes.string.isRequired,
    name: PropTypes.string.isRequired,
    label: PropTypes.string.isRequired,
    type: PropTypes.string,
  };

const Signup = (props) => {

  const [btn_disabled, setBtn_disabled] = useState(true);
  const [btn_color, setBtn_color] = useState('#BBB');
  // const [email, setEmail] = useState('');
  // const [senha, setSenha] = useState('');
  // const [senha2, setSenha2] = useState('')
  const [entrada, setEntrada] = useState([
    {email: ''},
    {senha: ''},
    {senha2: ''}
  ])

  function change(i) {
    const newState = {[i.target.name]: i.target.value}
    return (
      // setEmail(newState),
      // setSenha(newState),
      // setSenha2(newState)
      setEntrada(newState.length)
    )
  }

  function on_input (i) {
    change(i)    
    if (entrada[[0]] >= 3 && entrada[[1]] >= 3 && entrada[{senha2}] >= 3) {
      return (
        setBtn_disabled(false),
        setBtn_color('#000')
      )
    } else { 
        return (
        setBtn_disabled(true),
        setBtn_color('#BBB')
      )
    }
  }

console.log(entrada)


  return (
    <React.Fragment>
        <section className="telaCadastro">
            <div className="cadastro">
                <img className="logoPrincipal" src={logoPlaystars} alt="Logo PlayStarts" />
                <h2 className="tituloLogo">Olá Vamos começar seu cadastro?</h2>           
                <Form className="dados">
                    <div className="inputInline" name="email" onInput={on_input.bind(this)} onChange={change.bind(this)}>
                        <FormField id="email" name="email" type="text" label="Email"/>
                    </div>
                    <div className="inputInline" name="senha" onInput={on_input.bind(this)} onChange={change.bind(this)}>
                        <FormField id="senha" name="senha" type="password" label="Senha"/>
                    </div>
                    <div className="inputInline"  name="senha2" onInput={on_input.bind(this)} onChange={change.bind(this)}>
                      <FormField id="senha2" name="senha2" type="password" label="Confirme a senha"/>
                    </div>                  
                    <button className="continuar" 
                    style={{background: `${btn_color}` }} 
                    type="submit" 
                    disabled={btn_disabled}
                    required={props.isSubmitting}>Continuar</button>
                </Form>
            </div>
        </section>
    </React.Fragment>   
  )
}

const SignupForm = withRouter(withFormik({
    mapPropsToValues({
                       email,
                       senha,
                       senha2
                     }) {
      return {
        email: email || '',
        senha: senha || '',
        senha2: senha2 || ''
      }
    },
    validationSchema: Yup.object().shape({
      email: Yup.string()
        .required("Campo obrigatório")
        .email('Email inválido'),
      senha: Yup.string()
        .required("Campo obrigatório")
        .min(8, "Mínimo 8 caracteres"),
      senha2: Yup.string()
        .required("Campo obrigatório")
    }),
    handleSubmit({ email, senha, senha2 }, { props, setFieldError, setSubmitting }) {
      console.log(props);
      if(senha !== senha2) {
        setFieldError("senha", "As senhas digitadas não estão idênticas.");
        setFieldError("senha2", "As senhas digitadas não estão idênticas.");
        return;
      } 
      setSubmitting(true);

      Auth.signUp(email, senha)
        .then((result) => {
          console.log('Resultado do signup: ', result)
          console.log('funcionou')
          props.history.push('/codconfirmacao', { email: email });
          console.log('Minhas props no inicio do cadastro:',props)
        })
        .catch(error => {
          switch(error.code) {
            case "UsernameExistsException":
              setFieldError("email", "Email já está em uso.");
              break;
            default:
              console.log('Erro fazendo signup: ', error);
              window.alert('Erro fazendo signup');
              break;
          }
          setSubmitting(false);

        });
    }
  })(Signup));

  export default SignupForm;
