import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';

import { Auth } from "aws-amplify";
import PageContainer from "../../components/features/PageContainer";
import smartphone from "../../assets/icons/email.png";

import './styles.scss';

class EsqSenha extends Component {

    constructor(props) {
        super(props);
        this.state = {email: '', btn_disabled: true, btn_color: '#BBB'};
    };

    on_input() {  
        if (this.state.email.length >= 5) {
            this.setState({ btn_disabled: false});
            this.setState({ btn_color: '#000'})
        } else {
            this.setState({ btn_disabled: true})
            this.setState({ btn_color: '#BBB'})
        }
    }

    forgotPassword(ev) {
        ev.preventDefault()
        Auth.forgotPassword(this.state.email)
        .then(data => {
            console.log('NOVO CODIGO', data)
            this.props.history.push('/codigo-de-confirmacao', {email: this.state.email})
            console.log(this.props)
        })
        .catch(err => {
            console.log('ERROR', err)
            if(err.code === err.name) {
                this.props.history.push('/error', { message: err.message });
              }
        })
    }

    render() {
        return (
            <PageContainer public={true}>
            <div className="containerEsqSenha">
            <div className="lightBox">
                <div className="lightBoxContent">
                    <img src={smartphone} alt="Icone Cel"/>
                    <h3>Esqueceu sua senha?</h3>
                    <p className="paragraph">Insira o email que usa para entrar no PlayerStars</p>
                    <form className="formConfirm" 
                    autoComplete="off" 
                    onSubmit={this.forgotPassword.bind(this)}>
                        <fieldset>
                            <input id="email" type="email" name="email" onInput={this.on_input.bind(this)} onChange={(e) => {this.setState({email: e.target.value})}} required/>
                            <label className="label" htmlFor="email">Email</label>
                        </fieldset>
                        <button className="btnDefault" disabled={this.state.btn_disabled} style={{background: `${this.state.btn_color}` }}>Continuar</button>
                    </form>
                </div>
            </div>
        </div>
        </PageContainer>
        )
    }
}

export default withRouter(EsqSenha);
