import React, { Component } from 'react';
import { Auth } from "aws-amplify";
import { withRouter } from 'react-router-dom';
import Loader from '../../Loader/index';

import smartphone from './img/smartphone.png';

import './styles.scss';

class EsqSenha extends Component {

    state = {
        email: '', 
        btn_disabled: true, 
        btn_color: '#BBB',
        loading: false
    };

    on_input () {  
        if (this.state.email.length >= 5) {
            this.setState({ btn_disabled: false});
            this.setState({ btn_color: '#000'})
        } else {
            this.setState({ btn_disabled: true})
            this.setState({ btn_color: '#BBB'})
        }
    }

    forgotPassword(ev) {
        ev.preventDefault()
        this.setState({loading: true})
        Auth.forgotPassword(this.state.email)
            .then(data => {
                console.log('NOVO CODIGO', data)
                this.props.history.push('/codigoconfirmacao', { email: this.state.email })
            })
            .catch(err => console.log('ERROR', err));
    }

    renderEsq() {
        return (
            <div className="containerEsqSenha">
            <div className="lightBox">
                <div className="lightBoxContent">
                    <img src={smartphone} alt="Icone Cel"/>
                    <h3>Esqueceu sua senha?</h3>
                    <p className="paragraph">Insira o email que usa para entrar no PlayerStars</p>
                    <form className="formConfirm" autocomplete="off" onSubmit={this.forgotPassword.bind(this)}>
                        <fieldset>
                            <input id="email" type="email" name="email" onV={this.on_input.bind(this)} onChange={(e) => {this.setState({email: e.target.value})} }  required/>
                            <label className="label "htmlFor="email">Email</label>
                        </fieldset>
                        <button className="btnDefault" disabled={this.state.btn_disabled} style={{background: `${this.state.btn_color}` }}>Continuar</button>
                    </form>
                </div>
            </div>
        </div>
        )
    }

    renderLoading() {
        return (
        <div>
            <Loader />
        </div>
        )
    }

    render() {
        const content = (this.state.loading) ? (this.renderLoading()) : (this.renderEsq())
        return (
            <span>
            {content}
            </span>
        )
    }
}


const EsqueciPassword = withRouter(EsqSenha);

export default EsqueciPassword;
