import React, { useState } from 'react';
import {Link, withRouter} from 'react-router-dom';
import {Auth} from 'aws-amplify';
import PageContainer from "../../components/features/PageContainer";
import logoPlaystars from '../../assets/images/logo_playerStars.svg';
import iconFacebook from '../../assets/icons/facebook.svg';
import iconGoogle from '../../assets/icons/google_plus.svg';
import './styles.scss';
import Loader from '../../components/elements/Loader';


function Login (props) {

    const [email, setEmail] = useState('');
    const [senha, setSenha] = useState('');
    const [loading, setLoading] = useState(false);
    const [btn_disabled, setBtn_disabled] = useState(true);
    const [btn_color, setBtn_color] = useState('#bbb');

    const on_input = () => {             
        if (email.length >= 5 && senha.length >= 5) {
            setBtn_disabled(false);                
            setBtn_color('#000')            
        } else {                
            setBtn_disabled(true);                
            setBtn_color('#bbb')          
        }                  
    }

    const handleSubmit = (ev) => {
        ev.preventDefault()
        setLoading(true);
        Auth.signIn(email, senha)
          .then(response => {
            props.history.push('/')
          })
          .catch(error => {
            console.log('Erro fazendo login: ', error)
            if(error.code === 'UserNotConfirmedException') {
              props.history.push('/codigo-de-confirmacao', { email });
            }
            else {
                setLoading(false);
                alert( error.message )
            }
          })
      }

    return (
        <PageContainer register={true}>
            <section className="tela-de-login">
                <div className="login">
                    <img className="logo-principal" src={logoPlaystars} alt="Logo PlayStarts" />
                    <h2 className="titulo-logo">Acesse sua conta</h2>
                    <form className="acesso" onSubmit={handleSubmit}>
                        <fieldset>
                            <input id="email" 
                                type="email" 
                                name="email"
                                onInput={on_input} 
                                required 
                                onChange={e => setEmail(e.target.value)}
                            />
                            <label className="label" htmlFor="email">E-mail</label>
                        </fieldset>
                        <fieldset>
                            <input id="senha" 
                                type="password" 
                                name="senha" 
                                onInput={on_input} 
                                required 
                                onChange={e => setSenha(e.target.value)}
                            />
                            <label className="label" htmlFor="senha">Senha</label>
                        </fieldset>
                        <input 
                            className="btnEntrar" 
                            type="submit" 
                            disabled={btn_disabled} 
                            style={{background: `${btn_color}` }} 
                            value="Entrar"
                        />    
                    </form>
                    <div className="esqueci-senha">
                        <Link to="/esqueci-senha">Esqueci minha senha</Link>
                    </div>
                    <div className="redes-sociais">
                        <Link><img className="facebook-icon" src={iconFacebook} alt="Icone Facebook"></img></Link> 
                        <Link><img className="google-icon" src={iconGoogle} alt="Icone Google plus"></img></Link>
                    </div>
                    <div className="sem-conta">
                        <Link to="/cadastro">Ainda não tenho conta</Link>
                    </div>
                </div>
            </section>
        </PageContainer>
    )


        const renderLoading = () => (
            <div className="tela-de-login">
                <Loader />
            </div>
        )

        const content = (loading) ? (renderLoading()) : (renderForm())

    return (
        <span>
        {content}
        </span>
    )
}


const LoginForm = withRouter(Login)

export default LoginForm;
