import React, { Component } from 'react'
import PropTypes from 'prop-types'
import { Redirect } from "react-router-dom"

import { Auth } from "aws-amplify"
import Menu from "../../components/Header/Menu"
import Footer from "../../components/Footer"
// import {FireworkSpinner} from "react-spinners-kit"
import config from "../../config/constants"

const API = {
  get: (a, b, c) => new Promise((okfn) => okfn({entity_id: b}))
}

class View extends Component {
  state = {
    loading: true,
    redirect: false,
    user_data: undefined
  };

  redirect_to_error(error_message) {
    this.setState({
      redirect: true,
      loading: false,
      to: {
        pathname: "/",
        state: {
          message: error_message
        }
      }
    })
  }

  redirect_to_login() {
    this.setState({
      redirect: true,
      loading: false,
      to: {
        pathname: "/login"
      }
    })
  }

  redirect_to_cadastro() {
    this.setState({
      loading: false,
      redirect: true,
      to: {pathname: "/dadosuser"}
    })
  }

  get_user_data(cognito_response) {
    const user_id = cognito_response.attributes.sub
    API.get(config.APIS.PLAYERSTARS, `/user/${user_id}`, {})
      .then(response => {
        console.log('get user data bem sucedido. Response: ', response)
        this.setState({
          loading: false,
          user_data: {
            cognito_user: cognito_response,
            data: response.data
          }
        })
      })
      .catch(error => {
        console.log('get user data falhou: ', error)
        if (error.response && error.response.status === 404) {
          this.redirect_to_cadastro()
        } else {
          this.redirect_to_error('Erro obtendo dados do usu치rio')//error.response.message)
          console.log('Erro obtendo dados o usu치rio: ', error.response)
        }
      })
      console.log(this.props)
      console.log(this.props.cadastro)
      console.log(this.props.public)
      console.log(this.props.user_data)
  }

  get_authenticated_user() {
    Auth.currentAuthenticatedUser()
      .then(response => {
        console.log('get_authenticated_user, response:', response)
        console.log('Is cadastro view?', this.is_cadastro_view())
        if(!this.is_cadastro_view()) {
          this.get_user_data(response)
        }
        this.setState({
          loading: false, 
          user_data: {
            cognito_user: response
          }
        })
      })
      .catch(error => {
        if(!this.is_public_view()) {
          if (error === "not authenticated") {
            this.redirect_to_login()
          } else {
            this.redirect_to_error(error)
          }
        }
        else {
          this.setState({loading: false})
        }
      })
  }

  is_cadastro_view() {
    return this.props.cadastro
  }

  is_public_view() {
    return this.props.public
  }

  has_user_data() {
    return this.props.user_data !== undefined
  }
  componentDidMount() {
    console.log('view: componentDidMoutt: has_user_data:', this.has_user_data())
    if(!this.has_user_data()) {
      this.get_authenticated_user()
    }
  }

  // noinspection JSMethodCanBeStatic
  renderLoading() {
    return (
      <div className="Loading">
        <div><p stile="font-size:70">Carregando...</p></div>
      </div>
    )
  }

  renderContent() {
    const children = React.Children.map(this.props.children,
      child => React.cloneElement(child, {user_data: this.state.user_data}))

    return (
      <React.Fragment>
        {children}
      </React.Fragment>
    )
  }

  render() {
    if (this.state.redirect) {
      return <Redirect to={this.state.to}/>
    }

    const content = (this.state.loading) ? (this.renderLoading()) : (this.renderContent())
    return (
      <React.Fragment>
        <Menu user_data={this.state.user_data}/>
        {content}
        <Footer user_data={this.state.user_data}/>
      </React.Fragment>
    )
  }
}

View.propTypes = {
  background: PropTypes.any,
  backgroundHeight: PropTypes.string,
  public: PropTypes.bool,
  cadastro: PropTypes.bool
}

export default View
