import React, { useState } from 'react';
import { Redirect } from 'react-router-dom';
import config from "../../config/constants"
import { API } from "aws-amplify";
import PageContainer from "../../components/features/PageContainer";
import './styles.scss';
import TimelineCadastro from '../../components/features/TimelineCadastro';

function Termos (props) {

    const [btn_disabled, setBtn_disabled] = useState(true);
    const [btn_color, setBtn_color] = useState('#BBB');
    const [redirect, setRedirect] = useState(false);

    const on_scroll = (s) => {
        const target = s.target;
        const scrollPos = target.scrollTop;

        if (scrollPos > 700) {
            setBtn_disabled(false)
            setBtn_color('#000')
        }
    }

    const acceptTerms = () => {
        const options = {
            body:{
                terms: true
            }
        }

        API.post(config.APIS.PLAYERSTARS, '/player/accept-terms', options)
            .then(response => {
                setRedirect(true)
            })
            .catch(error => {
                console.log('Esse é o retorno das options', options);
                window.alert('Erro fazendo post dos dados do usuário.');
            })
    };


    return (
        <>
        <PageContainer public={true}>
            {redirect ? <Redirect to='/' /> : <div/>}
            <section className="Termos">
            <TimelineCadastro current={4} />
                <div className="containerTermos">
                    <h3 className="titleH3">Quase Pronto!</h3>
                    <p >Leia nossos Termos e Condições de Uso para finalizar o Cadastro.</p>
                    <div className="boxTermo" onScroll={on_scroll}>
                        <div className="boxContent">
                            <h3 className="titleH3">Termos e Condições de Uso</h3>
                            <p>1.1 - ACCEPTANCE THE USE OF LOREM IPSUM TERMS AND CONDITIONS</p>
                            <p>Your access to and use of Lorem Ipsum (the app) is subject exclusively to these Terms and Conditions.
                            You will not use the app for any purpose that is unlawful or prohibited by these Terms and Conditions.
                            By using the app you are fully accepting the terms, conditions and disclaimers contained in this notice.
                                If you do not accept these Terms and Conditions you must immediately stop using the app.</p>

                            <p>1.2 - CREDIT CARD DETAILS</p>
                            <p>All Lorem Ipsum purchases are managed by the individual App Stores (Apple, Google Windows)
                            and Lorem Ipsum will never store your credit card information or make it available to any third parties.
                            Any purchasing information provided will be provided directly from you to the respective App Store
                                and you will be subject to their credit card policies.</p>

                            <p>1.3 - LEGAL ADVICE</p>
                            <p>The contents of Lorem Ipsum app do not constitute advice and should not be relied upon in making
                            or refraining from making, any decision. All material contained on Lorem Ipsum is provided without any or warranty of any kind.
                                You use the material on Lorem Ipsum at your own discretion</p>

                            <p>2.1 - CHANGE OF USE</p>
                            <p>Your access to and use of Lorem Ipsum (the app) is subject exclusively to these Terms and Conditions.
                            You will not use the app for any purpose that is unlawful or prohibited by these Terms and Conditions.
                            By using the app you are fully accepting the terms, conditions and disclaimers contained in this notice.
                                If you do not accept these Terms and Conditions you must immediately stop using the app.</p>

                            <p>2.2 - LINKS TO THIRD PARTY APPS AND WEBSITES</p>
                            <p>Your access to and use of Lorem Ipsum (the app) is subject exclusively to these Terms and Conditions.
                            You will not use the app for any purpose that is unlawful or prohibited by these Terms and Conditions.
                            By using the app you are fully accepting the terms, conditions and disclaimers contained in this notice.
                                If you do not accept these Terms and Conditions you must immediately stop using the app.</p>

                            <p>3.0 COPYRIGHT</p>
                            <p>All Lorem Ipsum purchases are managed by the individual App Stores (Apple, Google Windows)
                            and Lorem Ipsum will never store your credit card information or make it available to any third parties.
                            Any purchasing information provided will be provided directly from you to the respective App Store
                                and you will be subject to their credit card policies.</p>

                            <p>4.1 LINKS TO AND FROM OTHER APPS AND WEBSITES</p>
                            <p>The contents of Lorem Ipsum app do not constitute advice and should not be relied upon in making
                            or refraining from making, any decision. All material contained on Lorem Ipsum is provided without any or warranty of any kind.
                                You use the material on Lorem Ipsum at your own discretion</p>

                            <p>4.2 DISCLAIMERS AND LIMITATION OF LIABILITY</p>
                            <p>Your access to and use of Lorem Ipsum (the app) is subject exclusively to these Terms and Conditions.
                            You will not use the app for any purpose that is unlawful or prohibited by these Terms and Conditions.
                            By using the app you are fully accepting the terms, conditions and disclaimers contained in this notice.
                                If you do not accept these Terms and Conditions you must immediately stop using the app.</p>

                            <p>5.0 INDEMNITY</p>
                            <p>All Lorem Ipsum purchases are managed by the individual App Stores (Apple, Google Windows)
                            and Lorem Ipsum will never store your credit card information or make it available to any third parties.
                            Any purchasing information provided will be provided directly from you to the respective App Store
                                and you will be subject to their credit card policies.</p>
                        </div>
                    </div>
                    <button
                        className="criarConta"
                        type="submit"
                        disabled={btn_disabled}
                        style={{ background: `${btn_color}` }}
                        onClick={acceptTerms}>
                        Criar conta
                    </button>
                </div>
            </section>
            </PageContainer>
        </>
    )

}

export default Termos;
