import React, { Component } from 'react';
import { Auth } from "aws-amplify";
import smartphone from '../../Cadastro/InicioDoCadastro/CodConfirmacao/img/smartphone.png';
import '../../Cadastro/InicioDoCadastro/CodConfirmacao/styles.scss';
import { withRouter } from 'react-router-dom';
import { withAlert } from 'react-alert';

class confirmCodigoRecSenha extends Component {

  constructor(props){
    super(props);
    this.state = {
      code: '',
      redirect: false
    };
  };

  setRedirect = () => {
    this.setState({
        redirect: true,
    })
  };

  on_input() {  
    if (this.state.code.length >= 5) {
        this.setState({ btn_disabled: false});
        this.setState({ btn_color: '#000'})
    } else {
        this.setState({ btn_disabled: true})
        this.setState({ btn_color: '#BBB'})
    }
}

  ValCode(ev) {
    ev.preventDefault()
    if (this.state.code.length === 5 && this.state.redirect === true) {
     this.props.history.push('/novasenha', {email: this.props.history.location.state.email, code: this.state.code});
      console.log(this.props)
    } else {
      this.props.alert.error('Codigo inválido');
    }
  }

  forgotPassword(ev) {
        ev.preventDefault()
        Auth.forgotPassword(this.props.history.location.state.email)
        .then(data => {
            console.log('NOVO CODIGO', data)
        })
        .catch(err => console.log('ERROR', err));
    }

  render() {
    return (
      <React.Fragment>
        <div className="containerCodConfirm">
            <div className="lightBox">
                <div className="lightBoxContent">
                    <img src={smartphone} alt="Icone Cel"/>
                    <h3>Confirme seu número de telefone</h3>
                    <p className="paragraph">Enviamos um código de confirmação por SMS para você. Por favor, digite-o abaixo.</p>
                    <form className="formConfirm" onSubmit={this.ValCode.bind(this)}>
                        <div className="inputInline">
                          <input id="codConfirm" type="text" name="codigo" onChange={(e) => {this.setState({code: e.target.value})} } required />
                          <label className="label" htmlFor="codConfirm">Código de confirmação</label>
                        </div>
                        <button className="btnDefault" onClick={this.setRedirect.bind(this)} required>Confirmar</button>
                        <button className="btnSecundary"  onClick={this.forgotPassword.bind(this)}>Reenviar Código</button> 
                    </form>
                </div>
            </div>
        </div>
      </React.Fragment>
    )
  }

};

export default withAlert()(withRouter(confirmCodigoRecSenha));


