import React, {useState} from 'react'
import config from '../../config/constants';
import { API } from "aws-amplify";
import './styles.scss';

function Contato () {

    const [nome, setNome] = useState('');
    const [email, setEmail] = useState('');
    const [assunto, setAssunto] = useState('');
    const [mensagem, setMensagem] = useState('');
    const [btn_disabled, setBtn_disabled] = useState(true);
    const [btn_color, setBtn_color] = useState('#BBB');

    const on_input = () => {
        if (nome.length >= 3 && email.length >= 3 && assunto.length >= 3 && mensagem.length >= 3) {
            setBtn_disabled(false);
            setBtn_color('#000');
        } else {
            setBtn_disabled(true);
            setBtn_color('#BBB')
        }
    }

    const sendContact = (e) => {
        e.preventDefault()
        const options = {
            body: {
                "subject": assunto,
                "contact_message": mensagem,
                "data": []
            }
        }

        API.post(config.APIS.PLAYERSTARS, '/contact-email/send', options)
            .then(response => {
                console.log('RESPONSE: ', response)
                window.alert("mensagem enviada");
                setNome(''),
                setEmail(''),
                setAssunto(''),
                setMensagem('')
            })
            .catch(error => {
                console.log("ERROR: ", error)
                window.alert('Erro fazendo post do contato do usuário.');
            })
    }
    return (
        <>
            <section className="containerContato">
                <div className="contentContato">
                    <h1 className="titleContato">Contato</h1>
                    <p className="paragrafyContato">Mollis nisl. Maecenas pulvinar turpis at faucibus posuere. Maecenas ut nibh non mauris dictum.</p>
                    <form className="formContato">
                        <fieldset>
                            <input id="nome" type="text" name="nome" value={nome} onChange={e => setNome(e.target.value)} onInput={this.on_input.bind(this)} required />
                            <label className="label" htmlFor="nome">Nome</label>
                        </fieldset>
                        <fieldset>
                            <input id="email" type="email" name="email" value={email} onChange={e => setEmail(e.target.value)} onInput={this.on_input.bind(this)} required />
                            <label className="label" htmlFor="email">E-mail</label>
                        </fieldset>
                        <fieldset>
                            <input id="assunto" type="text" name="assunto" value={assunto} onChange={e => setAssunto(e.target.value)} onInput={this.on_input.bind(this)}  required />
                            <label className="label" htmlFor="assunto">Assunto</label>
                        </fieldset>
                        <fieldset>
                            <textarea id="mensagem" name="mensagem" value={mensagem} onChange={e => setMensagem(e.target.value)} onInput={this.on_input.bind(this)} required></textarea>
                            <label className="label" htmlFor="mensagem">Mensagem</label>
                        </fieldset>
                        <button className="btnEnviar" type="submit" onClick={this.sendContact.bind(this)} disabled={this.state.btn_disabled} 
                            style={{background: `${this.state.btn_color}` }}>Enviar</button>
                    </form>
                </div>
            </section>
        </>
    )

}

export default Contato;
