import React, { Component } from 'react';
// import { Auth } from "aws-amplify";
import smartphone from '../../Cadastro/InicioDoCadastro/CodConfirmacao/img/smartphone.png';
import '../../Cadastro/InicioDoCadastro/CodConfirmacao/styles.scss';
import { withRouter } from 'react-router-dom';
import { Redirect } from 'react-router'


class confirmCodigoRecSenha extends Component {

  constructor(props){
    super(props);
    this.state = {
      code: '',
      envio: false
    };
  };

  ValCode() {
    if (parseInt(this.state.code.length) !== 6) {
      this.setState({envio: true})
      return <Redirect to='/novasenha'/>
    }
  }

  render() {
    return (
      <React.Fragment>
        <div className="containerCodConfirm">
            <div className="lightBox">
                <div className="lightBoxContent">
                    <img src={smartphone} alt="Icone Cel"/>
                    <h3>Confirme seu número de telefone</h3>
                    <p className="paragraph">Enviamos um código de confirmação por SMS para você. Por favor, digite-o abaixo.</p>
                    <form className="formConfirm">
                        <div className="inputInline">
                          <input id="codConfirm" type="text" name="codigo" onChange={(e) => {this.setState({code: e.target.value})} } label="Código de confirmação" required />
                        </div>
                        <button className="btnDefault" required>Confirmar</button>
                        <button className="btnSecundary">Reenviar Código</button> 
                    </form>
                </div>
            </div>
        </div>
      </React.Fragment>
    )
  }

};

export default withRouter(confirmCodigoRecSenha);
