import React, { useEffect, useState } from 'react'
import './styles.scss';
import { API } from "aws-amplify"
import config from '../../config/constants';
import Card from './Card/index';
import Select from '../Select';
// import Loading from './../Loader';

function ComponentHistorico () {

    const [historico, setHistorico] = useState([]);
    const [statusSelected, setStatusSelected] = useState('');
    // const [loading, setLoading] = useState(true)

    useEffect(() => {
         getHistory()
         // eslint-disable-next-line 
    }, [loading]) 

    const getHistory = () => {
        API.get(config.APIS.PLAYERSTARS, '/duel/get-my-duels/finished-under_review')
            .then(response => {
                console.log("ESSE É O RESPONSE DO HISTORY: ", response.data)
                setHistorico(response.data);
                // setLoading(false)
            })
            .catch(error => {
                console.log("ERRO NO GET: ", error)
                setHistorico(false)
            })
    }

    const options = ["Todos", "Finalizados", "Em apuração"]

    const changeStatus = (e) => {
        setStatusSelected(e)
        setHistorico(historico.filter((item) => {
            if(e === "Finalizados") {
                return item.status === "finished"
            } else if (e === "Em apuração") {
                return item.status === "under_review"
            } else {
                return item
            }
        }))
    }

    return (
        <section className="containerHistorico">
            <h1 className="titleHistorico">MEUS DUELOS</h1>
            <div className="containerSelects">
                <Select
                    onChange={changeStatus}
                    width={'150px'}
                    placeholder={statusSelected ? statusSelected : "Todos"}
                    options={options}
                    classNames={'select-class'}
                    name="statusDuel"
                />
            </div>
            {historico ? historico.map((item , index) => <Card 
                key={index}
                imageGame={item.gameImage}
                nomeGame={item.gameName}
                platform={item.consoleName}
                matchType={item.matchType}
                matchTitle={item.matchTitle}
                date={item.date}
                duration={item.durationMax}
                bet={item.bet}
                premium={item.reward}
                members={item.members}
                ranking={item.ranking}
                match={item.match}
                starType={item.star_type}
                winner={item.winner}
                opponentName={item.opponent_name}
                opponentType={item.opponent_type}
            />) : <p className="descriptionDuels">Você ainda não duelou! Depois que enfrentar oponentes no app, os jogos dos quais tiver participado aparecerão aqui.</p> }
        </section>
    )
}

export default ComponentHistorico;
