import React, { useState, useEffect } from 'react';
import { API } from "aws-amplify"
import config from "../../../config/constants"
import { useSelector } from 'react-redux';

import { withRouter, Redirect } from "react-router-dom";
import { withFormik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';


import './styles.scss';

function EditarConsoles() {
    const currentConsoles = useSelector(state => state.user.data.consoles);
    console.log(currentConsoles)

    const [userConsole, setUserConsole] = useState('');

    useEffect(() => {
        API.get(config.APIS.PLAYERSTARS, '/console/')
            .then(response => {
                console.log("ESSE E O RESPONSE", response.data)
                setUserConsole(response.data);
            })
            .catch(error => {
                console.log("ERRO NO GET: ")
            })
            // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])

  return (
    <section className="containerEditConsole">
        <div className="contentEditConsole">
            <h2 className="titleEditConsole">Editar plataformas</h2>
            <p className="subTitleEditConsole">Quais plataformas você usa?</p>
            <form className="dadosConsole">
                {userConsole.length > 0 ?
                    userConsole.map((platform, index) => (
                        <fieldset key={index}>
                            <input 
                                id={platform.name} 
                                type="text" 
                                name={platform.name} 
                                required/>
                            <label className="label" htmlFor={platform.name}>Tagname do {platform.name}</label>
                        </fieldset>
                    )) : <div></div>
                }
                <div className="containerBtn">
                    <button className="btnCancel" type="button">Cancelar</button>
                    <button className="btnSave" type="submit" >Salvar alterações</button>
                </div>
            </form>
        </div>
    </section>
  );
}

const SaveEdit = withRouter(withFormik({
    mapPropsToValues({
        name  
    }) {
        return {
            name: name || ''
        }
    },
    validationSchema: Yup.object().shape({
        name: Yup.string().required('Digite seu nome completo!')
    }),
    handleSubmit(values, { props, setValues }) {
        const options = {
            body: {
                user: {
                    name: values.name
                }
            }
        }
        API.put(config.APIS.PLAYERSTARS, '/player', options)
            .then(response => {
                console.log(":::::::OPTIONS:::::::", options)
                window.location.href = '/perfil'
            })
            .catch(error => {
                console.log(":::::::OPTIONS:::::::", options)
                window.alert('Erro fazendo PUT dos usuario!');
            })
    }
})(EditarConsoles));

export default SaveEdit
