import React, { Component } from 'react';
import config from '../../../config/constants';
import { API } from "aws-amplify";
import { Redirect } from 'react-router-dom';
import Loader from '../../Loader/index';

// import vitoria from '../img/vitoria.png';
import starBlue from '../img/star-blue.png';
import starGold from '../img/star-gold.png';
import editar from '../img/editar-perfil.png';
// import jogos from '../img/jogos.png';

import './styles.scss';

function RenderLoading() {
    return (
        <div className="Loading">
            <Loader />
        </div>
    )
}

class PerfilUsuario extends Component {

    state = {
        user: {},
        player: {},
        teams: [],
        duels: [],
        redirect: false,
        isLoading: true
    }


    componentDidMount() {
        this.getDadosProfile()
    }

    setRedirect = () => {
        this.setState({
            redirect: true,
        })
    };

    getDadosProfile() {
        API.get(config.APIS.PLAYERSTARS, '/player/get-my-profile')
            .then(response => {
                console.log("RESULTADO DO GET: ", response.data)

                this.setState({
                    user: response.data.player.user,
                    player: response.data.player,
                    teams: response.data.teams,
                    duels: response.data.duels,
                    isLoading: false
                })
            })
            .catch(error => {
                console.log("ERRO NO GET: ", error)
            })
    }

    render() {

        if (this.state.isLoading) {
            return <RenderLoading />
        }

        return (
            <>
                {this.state.redirect ? <Redirect to='/editarperfil' /> : <div></div>}
                {console.log("::::REDIRECT::", this.state.redirect)}
                <section className="containerProfile">

                    <div className="containerDadosUser">
                        <div className="headerDadosUser">
                            <div className="containerAvatarUser">
                                <div className="containerAvatar">
                                    <div className="avatar" style={{ backgroundImage: `url(${this.state.user.profile_image}?${Date.now()})` }}></div>
                                </div>

                                <div className="contentUser">
                                    <h3 className="nickname">{this.state.user.nickname}</h3>
                                    <span className="emailUser">{this.state.user.email}</span>
                                </div>
                            </div>
                            <div className="containerIconEditProfile" onClick={() => this.setRedirect(true)} >
                                <img className="iconEdit" src={editar} alt="Editar perfil" />
                                <span className="editProfile">Editar</span>
                            </div>
                        </div>
                        <div className="containerConquests">
                            {/* <div className="contentConquest">
                                <img className="iconConquest" src={vitoria} alt="Vitoria" />
                                <span className="descriptionConquest">Vitórias · {this.state.player.wins}</span>
                            </div> */}
                            <div className="contentConquest">
                                <img className="iconConquest" src={starBlue} alt="StarBlue" />
                                <span className="descriptionConquest">Star Blue · {this.state.player.blue_star_balance}</span>
                            </div>
                            <div className="contentConquest">
                                <img className="iconConquest" src={starGold} alt="StarGold" />
                                <span className="descriptionConquest" alt="StarBlue">Star Gold · {this.state.player.golden_star_balance}</span>
                            </div>
                        </div>


                        <div className="containerDadosAddress">
                            <div className="contentDados">
                                <h3 className="titleDadosAddress">Dados pessoais</h3>
                                <p className="descriptionDados">{this.state.user.name}</p>
                                <div className="containerNasCpfTel">
                                    <div className="contentNasCpfTel">
                                        <span className="titleNasCpfTel">Nascimento</span>
                                        <span className="descriptionNasCpfTel">{this.state.user.date_birth}</span>
                                    </div>
                                    <div className="contentNasCpfTel">
                                        <span className="titleNasCpfTel">Cpf</span>
                                        <span className="descriptionNasCpfTel">{this.state.user.cpf}</span>
                                    </div>
                                    {/* <div className="contentNasCpfTel">
                                        <span className="titleNasCpfTel">Telefone</span>
                                        <span className="descriptionNasCpfTel">{this.state.user.phone_number}</span>
                                    </div> */}
                                </div>
                            </div>
                            <div className="contentDados">
                                <h3 className="titleDadosAddress">Endereço</h3>
                                <p className="descriptionAddress">
                                    {this.state.user.street} , {this.state.user.street_number}
                                </p>
                                <p className="descriptionAddress">
                                    {this.state.user.neighborhood}, {this.state.user.postal_code} - {this.state.user.city} - {this.state.user.state}
                                </p>
                            </div>
                        </div>
                    </div>


                    <div className="containerPlatform">
                        <h2 className="titlePlatform">Minhas Plataformas</h2>
                        <div className="containerIconEditConsole">
                            <img className="iconEdit" src={editar} alt="Editar console" />
                            <Link></Link><span className="editConsole">Editar</span>
                        </div>
                        <div className="consoleName">
                            {this.state.player.consoles.map((item) => (<div className="consoleTagname">{item.name} · {item.tag_name}</div>))}                           
                        </div>
                    </div>
                    {/* <div className="containerGames">
                        <h2 className="titleGames">Jogos Duelados</h2>
                        <div className="containerPlayed">
                            {this.state.duels && this.state.duels.length > 0
                                ? this.state.duels.map((product, index) => (
                                    <img className="imageGame" src={product.gameImage} alt={product.game} key={index} />
                                ))
                                : <><img className="iconGames" src={jogos} alt="Jogos Duelados" />
                                    <p className="descriptionGames">Você ainda não duelou! Depois que enfrentar oponentes no app, os jogos dos quais tiver participado aparecerão aqui.</p></>
                            }
                        </div>
                    </div> */}
                    {/* <div className="containerTeam">
                        <h2 className="titleTeam">Meus times</h2>
                        <div className="subContentTeam">
                        {this.state.teams && this.state.teams.length > 0
                            ? this.state.teams.map((product, index) => (
                                <div className="containerTeams" key={index}>
                                    <div className="contentTeam">
                                        <div className="avatarTeam">
                                        </div>
                                        <span className="nameTeam">{product.name}</span>
                                        <span className="patent">{product.patent}</span>
                                    </div>
                                </div>
                            ))
                            :
                            <>
                                <img className="iconTeam" src={group} alt="Jogos Duelados" />
                                <p className="descriptionTeam">Quando criar ou entrar em times no app, eles aparecerão aqui.</p>
                            </>
                        }
                        </div>
                    </div> */}
                </section>
            </>
        )
    }
}


export default PerfilUsuario;
