import * as Yup from 'yup';
import Moment from "moment"
// let parseFormats = ['dd/mm/YYYY'];
let invalidDate = new Date('');

export default Yup.date().transform(function(value, originalValue) {
  // if (this.isType(value)) return value;
  // // the default coercion transform failed so lets try it with Moment instead
  value = Moment(originalValue, 'DD/MM/YYYY');
  return value.isValid() ? value.date() : invalidDate;
});
