import React, { Component } from 'react';
import { Link } from 'react-router-dom';

import './styles.scss';

import logo from './img/logo-menu.png'
import iconLogin from './img/icon-login.png';
import iconBrasil from './img/icon-brasil.png';
import iconEua from './img/icon-eua.png';
import iconEspanha from './img/icon-espanha.png';

import MenuMobile from './MenuMobile';
import BtnMenu from './BtnMenu';


class Menu extends Component {

    constructor(props) {
		super(props);
		this.state = {
			menuIsOpen: false
		};
	};

	toggleMenu() {
		this.setState({ menuIsOpen: !this.state.menuIsOpen });
	}

    render() {
        return (
            <React.Fragment>
                <MenuMobile isLogged={this.props.user_data !== undefined} isOpen={ this.state.menuIsOpen } />
                    <BtnMenu 
                        onClick={ this.toggleMenu.bind(this) }
                        isOpen={ this.state.menuIsOpen }
                    />
                <div className="menu">

                    <nav className="menu-navegacao">
                        <img className="menu-logo" src={logo} alt="Logo PlayStarts"/>
                        <ul className="menu-navegacao-itens">
                            <li><Link className="menu-navegacao-item" to="/">Como Jogar</Link></li>
                            <li><Link className="menu-navegacao-item" to="/regras">Regras</Link></li>
                            <li><Link className="menu-navegacao-item" to="/compras">Compre Stars</Link></li>
                            <li><Link className="menu-navegacao-item" to="/">Ranking</Link></li>
                            <li><Link className="menu-navegacao-item" to="/contato">Contato</Link></li>
                            <li><Link className="menu-navegacao-item" to="/cadastro">Participe</Link></li>
                        </ul>
                        <div className="menu-login">
                            <div className="autenticacao">

                                {this.props.user_data !== undefined ? (
                                    <div>
                                        <Link to="/logout" className="autenticacao-login">Sair</Link>

                                        <div className="contentHeader">
                                            <img className="avatar" 
                                            src={user.profile_image} 
                                            alt="Imagem de perfil" />
                                            <div className="user">
                                                <span className="nameUser">{user.nickname}</span>
                                                <div className="emailUser">{user.email}</div>
                                            </div>
                                        </div>

                                        <Link className="userLinks" to="/perfil">Meu Perfil</Link>
                                        <Link className="userLinks" to="/">Converter Stars</Link>
                                        <Link className="userLinks" to="/">Resgatar Star Gold</Link>
                                        <Link className="userLinks" to="/">Histórico de Compras</Link>
                                        <Link className="userLinks" to="/">Histórico de Partidas</Link>
                                    </div>

                                ) : (
                                    <Link to="/login" className="autenticacao-login">Entra</Link>
                                )}


                                <img className="menu-login-icon-login" src={iconLogin} alt="Icone Login"/>
                                <div className="bandeiras">
                                    <img className="menu-login-icon-language" src={iconBrasil} alt="Icone Idioma"/>
                                    <div className="idiomas">
                                        <p className="titulo-idiomas">Escolha um idioma</p>
                                        <ul>
                                            <li> <img className="menu-login-icon-language" src={iconBrasil} alt="Icone Idioma"/> Inglês</li>
                                             <li> <img className="menu-login-icon-language" src={iconEspanha} alt="Icone Idioma"/>Espanhol</li>
                                            <li> <img className="menu-login-icon-language" src={iconEua} alt="Icone Idioma"/>Português</li>
                                        </ul>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </nav>
                </div>
            </React.Fragment>
        )
    }
}

export default Menu;
