import React, { useState, useEffect } from 'react';
import {API} from "aws-amplify";
import config from "../../../config/constants";
import Select from '../../Select/index';

import './styles.scss';

function Filter(props) {

    const [plataformSelected, setPlataformSelected] = useState('')
    const [gameSelected, setGameSelected] = useState('')

    const [userConsole, setUserConsole] = useState('');
    useEffect(() => {
        API.get(config.APIS.PLAYERSTARS, '/console/')
            .then(response => {
                console.log("ESSE E O RESPONSE", response.data)
                setUserConsole(response.data);
            })
            .catch(error => {
                console.log("ERRO NO GET: ")
            })
            // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])

    let currentConsoleGames = !!plataformSelected.length && userConsole.find((specificConsole) => specificConsole.name === plataformSelected).games
    let FormattedJSONConsolesGames = !!plataformSelected.length && currentConsoleGames

    useEffect(() => {
        changePlatform(userConsole[0])
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])

    useEffect(() => {
        if(plataformSelected && !gameSelected)
        changeGame(FormattedJSONConsolesGames[0])
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [plataformSelected])

    function changePlatform(c) {
        console.log("PLATFORM :: ", c)
        setPlataformSelected(c.name)
        props.setFilter("platform", c)
    }

    function changeGame(c) {
        console.log("GAME :: ", c)
            setGameSelected(c.name)
            props.setFilter("game", c)
            console.log(":::GAME JOGO::::::", gameSelected)
    }

    return (
        <div className="containerFilter">
            <div className="containerSelects">
                <div className="containerSelect">
                    <Select
                        onChange={changePlatform}
                        width={'150px'}
                        // placeholder={userConsole.find(item => item.name === plataformSelected) ? plataformSelected : 'Plataforma'}
                        options={userConsole}
                        classNames={'select-class'}
                        name="platform"
                    />
                </div>
                <div className="containerSelect">
                    <Select
                        onChange={changeGame}
                        width={'150px'}
                        placeholder={FormattedJSONConsolesGames && FormattedJSONConsolesGames.find(item => item.name === gameSelected) ? gameSelected : 'Jogo'}
                        options={!!plataformSelected.length ? FormattedJSONConsolesGames : []}
                        classNames={'select-class'}
                        name="game"
                    />
                </div>
            </div>
        </div>
    )
}

export default Filter;
