import React from "react";
import "../../../styles/base.scss";

import IconeTemp from "../../../assets/icons/smartphone.png";
import IconeTempTwo from "../../../assets/icons/senha.png";

const AlertTemplate = ({ message, options }) => {
  const _menssage = message;
  const _options = options;
  return (
    <>
      <div
        style={{
          position: "fixed",
          top: "0",
          left: "0",
          bottom: "0",
          transform: "translate(-50%, -50%)",
          background: "rgba(0, 0, 0, 0.8)",
          height: "200vh",
          width: "200vw",
          zIndex: "10",
        }}
      ></div>
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          position: "fixed",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          width: "318px",
          height: "220px",
          borderRadius: "4px",
          backgroundColor: "#fff",
          textAlign: "center",
          textJustify: "center",
          padding: "0 20px",
          color: "#000",
          fontSize: "18px",
          zIndex: "10",
        }}
      >
        <img
          src={options.type === error ? IconeTempTwo : IconeTemp}
          style={{
            width: "64px",
            height: "64px",
            margin: "20px 0 30px",
          }}
          alt="icone de Status"
        />
        {_menssage}
      </div>
    </>
  );
};

export default AlertTemplate;
