import React from 'react';
import { useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';

import logo from '../img/logo-menu.png';

import './styles.scss';




const MenuMobile = (props) => {

    const user = useSelector(state => state.user.data.user);
    let MenuClass = "menuMobile";
    if (props.isOpen) {
        MenuClass += " open";
    }

    return (
        <nav className={MenuClass}>
            {console.log("::::::::::::::::::::::::::::USER_DATA::::", user)}
            <div className="headerContainer">
                {props.isLogged && user !== undefined? (
                    <div className="contentHeader">
                        <img className="avatar" src={user.profile_image} alt="Imagem de perfil" />
                        <span className="user">
                            <div className="nameUser">{user.nickname}</div>
                            <div className="emailUser">{user.email}</div>
                        </span>
                    </div>
                ) : (
                        <img src={logo} alt="Logo" />
                    )}
            </div>
            <Link className="mobileLinks" to="/">In√≠cio</Link>
            <Link className="mobileLinks" to="/comojogar">Como Jogar</Link>
            <Link className="mobileLinks" to="/regras">Regras</Link>
            <Link className="mobileLinks" to="/compras">Compre Stars</Link>
            <Link className="mobileLinks" to="/">Ranking</Link>
            <Link className="mobileLinks" to="/contato">Contato</Link>
            {props.isLogged ? (<Link className="mobileLinks" to="/editarperfil">Editar Perfil</Link>) : (<Link className="mobileLinks" to="/cadastro">Participe</Link>)}
            {props.isLogged ? (<Link className="mobileLinks" to="/logout">Sair</Link>) : (<Link className="mobileLinks" to="/login">Entrar</Link>)}
        </nav>
    );
};


MenuMobile.propTypes = {
    isOpen: PropTypes.bool.isRequired
};

export default MenuMobile;
