import React, { Component } from 'react';
import { withFormik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';

import './styles.scss';

const schema = Yup.object().shape({
    // pc: Yup.string().required('')
})

const enhanceWithFormik = withFormik({
  mapPropsToValues: () => ({ 
    pc: '',
    playstation: '',
    xbox: ''
}),
  handleSubmit: values => {
    console.log(values)
  },
  isInitialValid: false,
  validateOnChange: true,
  validateOnBlur: true,
  displayName: 'ConsoleComponet',
  validationSchema: schema
})


class ConsoleComponet extends Component {

    state = {
        btn_disabled: true,
        btn_color: '#BBB',
        required: true
    };

    on_input (i) {
        const target = i.target;
        const habilitar = target.value.length;

        if (habilitar > 0) {
            this.setState({ btn_disabled: false})
            this.setState({ btn_color: '#000'})
            this.setState({ required: false })
        } else {
            this.setState({ btn_disabled: true})
            this.setState({ btn_color: '#BBB'})
            this.setState({ required: true})
        }
    }

    render() {
        return (       
            <section className="telaConsole">
                <div className="conssoles">
                    <h3 className="titleH3">Insira a Tagname de suas plataformas</h3>          
                    <Form className="dadosConsole">
                        <ErrorMessage className="error" component="span" name="pc"/>
                            <fieldset>
                                <Field id="pc" type="text" name="pc" onInput={this.on_input.bind(this)} required={this.state.required}/>
                                <label className="label" htmlFor="pc">Tagname do PC</label>
                            </fieldset>
                            <fieldset>
                                <Field id="playstation" type="text" name="playstation" onInput={this.on_input.bind(this)} required={this.state.required}/>
                                <label className="label" htmlFor="playstation">Tagname do Playstation 4</label>
                            </fieldset>
                            <fieldset>
                                <Field id="xbox" type="text" name="xbox" onInput={this.on_input.bind(this)} required={this.state.required}/>
                                <label className="label" htmlFor="xbox">Tagname do Xbox</label>
                            </fieldset>
                            <button
                                className="continuar"
                                type="submit" disabled={this.state.btn_disabled}
                                style={{background: `${this.state.btn_color}` }}
                            >
                                Continuar
                            </button>
                    </Form>
                </div>
            </section>
        )
    }
}

export default enhanceWithFormik(ConsoleComponet);
