import React from "react";
import "../../../styles/base.scss";

const AlertTemplate = ({ message, close }) => {
  const _menssage = message;
  return (
    <>
      <div
        style={{
          display: "block",
          position: "fixed",
          top: "50%",
          left: "50%",
          background: "rgba(0, 0, 0, 0.8)",
          height: "100vh",
          width: "100vw",
          zIndex: "10",
        }}
      ></div>
      <div
        style={{
          display: "block",
          position: "fixed",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          width: "300px",
          borderRadius: "4px",
          backgroundColor: "#f9f9f9",
          textAlign: "center",
          textJustify: "center",
          padding: "20px",
          color: "#000",
          fontSize: "18px",
          zIndex: "10",
        }}
      >
        {_menssage}
      </div>
      <button onClick={close}>X</button>
    </>
  );
};

export default AlertTemplate;
