import React, { Component } from 'react';

import { withAlert } from 'react-alert';

import newsenha from './img/nova-senha.png';

import './styles.scss';

class NovaSenha extends Component {   

    state = {
        newPassword: '',
        confNewPassword: '',
        btn_disabled: true,
        btn_color: '#BBB',
    };


    handleChange(ev) {
        const newState = {[ev.target.name]: ev.target.value}
        this.setState(newState)
      }

    on_input (ev) {
        this.handleChange(ev)              
        if (this.state.newPassword.length >= 5 && this.state.confNewPassword.length >= 5) {
            this.setState({ btn_disabled: false});                
            this.setState({ btn_color: '#000'})            
        } else {                
            this.setState({ btn_disabled: true})                
            this.setState({ btn_color: '#BBB'})            
        }                
    }

    valPassword (props) {
        const alert = tprops.alert;
        if(this.state.newPassword !== this.state.confNewPassword) {
            alert.show("As senhas digitadas não estão idênticas.");
            return;
        }
    }

    render() {
        console.log(" :::::::" ,this.props.alert)
        this.props.alert.show("As senhas digitadas não estão idênticas.");
        return (
            <div className="containerNovaSenha">
                <div className="lightBox">
                    <div className="lightBoxContent">
                        <img src={newsenha} alt="Icone Cel"/>
                        <h3>Criar nova senha</h3>
                        <p className="paragraph">Agora é só redefnir sua senha!</p>
                        <form className="formConfirm">
                            <fieldset>
                                <input id="newPassword" type="password"  onInput={this.on_input.bind(this)} onChange={this.handleChange.bind(this)} name="newPassword" required />
                                <label className="label" htmlFor="newPassword">Nova Senha</label>
                            </fieldset>
                            <fieldset>
                                <input id="confNewPassword" type="password" onInput={this.on_input.bind(this)} onChange={this.handleChange.bind(this)} name="confNewPassword" required />
                                <label className="label" htmlFor="confnewpassword">Repetir nova senha</label>
                            </fieldset>

                            <button className="btnDefault" 
                            disabled={this.state.btn_disabled} 
                            style={{background: `${this.state.btn_color}` }}
                            onClick={this.valPassword.bind(this)} 
                            >Confirmar</button>
                        </form>
                    </div>
                </div>
            </div>
        )
    }   
}

export default withAlert()(NovaSenha);
