import React, { useState } from "react";
import PropTypes from 'prop-types';
import "./styles.scss";

const Modal = props => {
  const {title = "title modal", children = "content modal", btnText = "Text btn"} = props;

  const [open, setOpen] = useState({ display: "block" });
  const fechar = () => setOpen({ display: "none" });

  return (
    <>
      <div style={open} className="opacity"></div>
      <div style={open} className="box">
        <h3 className="title">{title}</h3>
        <div className="text">{children}</div>
        <button className="btn" onClick={fechar}>
          {btnText}
        </button>
      </div>
    </>
  );
};

Modal.propTypes = {
  title: PropTypes.string.isRequired,
  btnText: PropTypes.string,
};

export default Modal;
