import React from 'react';

import iconNext from '../img/Direita.png';
import iconPreview from '../img/Esquerda.png';
import ReactPaginate from 'react-paginate';

import './styles.scss';

function Navigation(props) {

    // const { pagination, setPage } = props
    const { page, total, per_page } = props.pagination

    const isActived = (_page) => {
        if (page === _page)
            return "activated"
        return ""
    }

    const prevPage = () => {
        if(page > 1)
        props.setPage(page - 1)
    }

    const nextPage = () => {
        console.log("nextPage")
        if(page < countPages ){
           props.setPage(page + 1)
        }
    }

    const countPages =  ((total - (total % per_page)) / per_page) +((total % per_page) !== 0 ? 1 : 0)
    console.log(":::::CONUNT PAGE", countPages )
    const listPages = new Array(parseInt(countPages)).fill(null)
    console.log(":::::LIST PAGE", listPages)
    const marginOffset = () => {
        if(countPages - page < 4 && countPages > 3){
            return -((countPages-4)*42)
        }
        return -((page-1)*42)
    }

    // const width = () => {
    //     if(countPages < 4){
    //         return countPages*42
    //     }
    //     return 4*42
    // }

    return (
        // <div className="containerNavigation">
        //     <div className="icon" onClick={prevPage}><img src={iconPreview} alt="next" /></div> 
        //     <div className="navigationBody" style={{width: width()}}>
        //         <div className="navigationList" style={{marginLeft: marginOffset()}}>
        //             {listPages.map((item, index) => {
        //                 const current = index + 1
        //                 return <span onClick={()=>props.setPage(current)} key={index} className={`page ${isActived(current)}`}> {current} </span>
        //             }
        //             )}
        //         </div>
        //     </div>
        //     {countPages > 4 && page < countPages - 3 && <span className={`page`}> ... </span>}

        //     <div className="icon" onClick={nextPage}><img src={iconNext} alt="next" /></div>
        // </div>
        <span className="containerNavigation">
            <ReactPaginate
                previousLabel={<div className="icon" onClick={prevPage}><img src={iconPreview} alt="next" /></div>}
                nextLabel={<div className="icon" onClick={nextPage}><img src={iconNext} alt="next" /></div>}
                breakLabel={<span>...</span>}
                pageCount={countPages}
                onPageChange={<div className="navigationList" style={{marginLeft: marginOffset()}}>
                            {listPages.map((item, index) => {
                                const current = index + 1
                                return <span onClick={()=>props.setPage(current)} key={index} className={`page ${isActived(current)}`}> {current} </span>
                            }
                            )}
                        </div>}
            />
        </span>
    )
}

export default Navigation;
