import React, { Component } from 'react';
import { withFormik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import PropTypes from 'prop-types';

import logoPlaystars from './../img/logo_final.svg';
import './styles.scss';
import { withRouter } from 'react-router-dom';
import FormField from "../FormField";
import { Auth } from "aws-amplify";

FormField.propTypes = {
    id: PropTypes.string.isRequired,
    name: PropTypes.string.isRequired,
    label: PropTypes.string.isRequired,
    type: PropTypes.string,
  };


// const schema = Yup.object().shape({
//     email: Yup.string().email('Email invalido!').required()
// })

// const enhanceWithFormik = withFormik({
//   mapPropsToValues: () => ({ email: '', senha: '', repitaSenha: ''}),
//   isInitialValid: false,
//   validateOnChange: true,
//   validateOnBlur: true,
//   displayName: 'InitCadastro',
//   validationSchema: schema
// });


const Signup = (props) => (

    // state = {
    //     btn_disabled: true,
    //     btn_color: '#BBB',
    // };

    // on_input (i) {
    //     const target = i.target;
    //     const habilitar = target.value.length;

    //     if (habilitar > 6) {
    //         this.setState({ btn_disabled: false});
    //         this.setState({ btn_color: '#000'})
    //     } else {
    //         this.setState({ btn_disabled: true});
    //         this.setState({ btn_color: '#BBB'})
    //     }
    // }  

    // render() {
    //     return (
            <React.Fragment>
                <section className="telaCadastro">
                    <div className="cadastro">
                        <img className="logoPrincipal" src={logoPlaystars} alt="Logo PlayStarts" />
                        <h2 className="tituloLogo">Olá Vamos começar seu cadastro?</h2>           
                        <Form className="dados">
                            <ErrorMessage className="error" component="span" name="email"/>
                            <fieldset>
                                <Field id="email" type="email" name="email" required/>
                                <label className="label" htmlFor="email">Email</label>
                            </fieldset>
                            <fieldset>
                                <Field id="senha" type="password" name="senha" required/>
                                <label className="label" htmlFor="senha">Senha</label>
                            </fieldset>
                            <fieldset>
                                <Field id="repitaSenha" type="password" name="repitaSenha" onInput={this.on_input.bind(this)} required/>
                                <label className="label" htmlFor="RepitaSenha">Repita a senha</label>
                            </fieldset>
                            <button
                                className="continuar"
                                type="submit" disabled={this.state.btn_disabled , props.isSubmitting}
                                style={{background: `${this.state.btn_color}` }}
                            >
                                Continuar
                            </button>
                        </Form>
                    </div>
                </section>
            </React.Fragment>
    //     )
    // }
)

const SignupForm = withRouter(withFormik({
    mapPropsToValues({
                       email,
                       senha,
                       senha2
                     }) {
      return {
        email: email || '',
        senha: senha || '',
        senha2: senha2 || ''
      }
    },
    validationSchema: Yup.object().shape({
      email: Yup.string()
        .required("Campo obrigatório")
        .email('Email inválido'),
      senha: Yup.string()
        .required("Campo obrigatório")
        .min(8, "Mínimo 8 caracteres"),
      senha2: Yup.string()
        .required("Campo obrigatório")
    }),
    handleSubmit({ email, senha, senha2 }, { props, setFieldError, setSubmitting }) {
      console.log(props);
      if(senha !== senha2) {
        setFieldError("senha", "As senhas digitadas não estão idênticas.");
        setFieldError("senha2", "As senhas digitadas não estão idênticas.");
        return;
      }
      setSubmitting(true);

      Auth.signUp(email, senha)
        .then((result) => {
          props.history.push('/confirma-assinar', { email: email });
        })
        .catch(error => {
          switch(error.code) {
            case "UsernameExistsException":
              setFieldError("email", "Email já está em uso.");
              break;
            default:
              console.log('Erro fazendo signup: ', error);
              window.alert('Erro fazendo signup');
              break;

          }
          setSubmitting(false);

        });
    }
  })(Signup));

  export default SignupForm;
