import React from "react";
import "../../../styles/base.scss";

const AlertTemplate = ({ message, close }) => {
  const _menssage = message;
  return (
    <>
      <div
        style={{
            position: "fixed",
            top: "0",
            left: "0",
            bottom: "0",
            transform: "translate(-50%, 50%)",
            background: "rgba(0, 0, 0, 0.8)",
            height: "100vh",
            width: "100vw",
            zIndex: "10",
            padding: "-50%",
        }}
      ></div>
      <div
        style={{
            position: "fixed",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            width: "300px",
            borderRadius: "4px",
            backgroundColor: "#fff",
            textAlign: "center",
            textJustify: "center",
            padding: "20px",
            color: "#000",
            fontSize: "18px",
            zIndex: "10",
        }}
      >
        {_menssage}
      </div>
      <button onClick={close}>X</button>
    </>
  );
};

export default AlertTemplate;
