import React, { Component } from 'react';
import { Auth } from "aws-amplify";
import { withRouter } from 'react-router-dom';
import { withAlert } from 'react-alert';

import newsenha from './img/nova-senha.png';

import './styles.scss';

class NovaSenha extends Component {

    constructor(props) {
    super(props);    
        this.state = {
            newPassword: '',
            confNewPassword: '',
            btn_disabled: true,
            btn_color: '#BBB'
        };
    };

    handleChange(ev) {
        const stateGeneric = { [ev.target.name]: ev.target.value }
        this.setState(stateGeneric)
    }

    on_input(ev) {
        this.handleChange(ev)
        if (this.state.newPassword.length >= 5 && this.state.confNewPassword.length >= 5) {
            this.setState({ btn_disabled: false });
            this.setState({ btn_color: '#000' })
        } else {
            this.setState({ btn_disabled: true })
            this.setState({ btn_color: '#BBB' })
        }
    }

    forgotPasswordSubmit(ev) {
        ev.preventDefault()
        if (this.state.newPassword !== this.state.confNewPassword) {
            this.props.alert.error("As senhas digitadas não estão idênticas.");
        } else {
            Auth.forgotPasswordSubmit(this.props.history.location.state.email, this.props.history.location.state.code, this.state.newPassword)
            .then(data => {
                console.log('NOVO SENHA', data)
                this.props.history.push('/login')
                console.log(this.props)
                this.props.alert.success('Senha Trocada com Sucesso')
            })
            .catch(err => {
                console.log('ERROR', err)
                if(err.code === 'CodeMismatchException') {
                    this.props.history.push('/codconfirmacao', { email: this.props.history.location.state.email });
                    this.props.alert.error('Código invalido')
                } else  {
                    this.props.history.push('/error', { message: err.message });
                }
            })
        }
    }


    render() {
        return (
            <div className="containerNovaSenha">
                <div className="lightBox">
                    <div className="lightBoxContent">
                        <img src={newsenha} alt="Icone Cel" />
                        <h3>Criar nova senha</h3>
                        <p className="paragraph">Agora é só redefnir sua senha!</p>
                        <form className="formConfirm" onSubmit={this.forgotPasswordSubmit.bind(this)}>
                            <fieldset>
                                <input id="newPassword" type="password" onInput={this.on_input.bind(this)} onChange={this.handleChange.bind(this)} name="newPassword" required />
                                <label className="label" htmlFor="newPassword">Nova Senha</label>
                            </fieldset>
                            <fieldset>
                                <input id="confNewPassword" type="password" onInput={this.on_input.bind(this)} onChange={this.handleChange.bind(this)} name="confNewPassword" required />
                                <label className="label" htmlFor="confnewpassword">Repetir nova senha</label>
                            </fieldset>

                            <button
                                className="btnDefault"
                                disabled={this.state.btn_disabled}
                                style={{ background: `${this.state.btn_color}` }}
                            >Confirmar</button>
                        </form>
                    </div>
                </div>
            </div>
        )
    }
}

export default withAlert()(withRouter(NovaSenha));
