import React, { Component } from 'react';
import { Redirect } from 'react-router'
import config from '../../../config/constants';
import { API } from "aws-amplify"

import starblue from './img/star-blue.png';
import stargold from './img/star-gold.png';
import converter from './img/converter.png';
import resgatar from './img/resgatar-stars.png';

import './styles.scss';

class SobreCompras extends Component {

    state = {
        redirect: '',
        productsBlue: [],
        productsGold: [],
    };

    componentDidMount() {
        this.getPurchase();
    };


    setRedirect = (router) => {
        this.setState({
            redirect: router,
        })
    };

    redirectPagSeguro(product) {
        console.log("PRODUCT: ", product)

        const options = {
            body: {
                "price": product.price,
                "description": product.description,
                "product_id": product.entity_id,
                "star_value": product.star_value,
                "star_type": product.star_type
            }
        }

        API.post(config.APIS.PLAYERSTARS, '/purchase', options)
            .then(response => {
                window.location.href = response.data.url_redirect
            })
            .catch(error => {
                console.log("ERROR: ", error)
                window.alert('Erro fazendo post dos dados do usuÃ¡rio.');
            })
    }

    getPurchase() {
        API.get(config.APIS.PLAYERSTARS, '/product')
            .then(response => {
                console.log("RESULTADO DO GET: ", response)
                const productsGold = response.data.gold
                const productsBlue = response.data.blue
                this.setState({
                    productsGold,
                    productsBlue
                })
            })
            .catch(error => {
                console.log("ERRO NO GET: ", error)
            })
    }


    render() {
        return (
            <React.Fragment>
                {this.state.redirect && <Redirect to={this.state.redirect} />}
                <section className="containerSobreCompras">
                    <div className="contentSobreCompras">
                        <div className="containerDescricao">
                            <h1>Compre STARS</h1>
                            <p>Blandit porta id ac augue. Vestibulum pulvinar sollicitudin ultricies. Phasellus feugiat
                                , ipsum sed condimentum ornare, eros lorem condimentum nibh, condimentum aliquam tellus
                            neque et nisi.</p>
                            <p>raesent velit ipsum, faucibus a ante vitae, aliquam viverra leo.
                                Integer porttitor accumsan nisi vehicula convallis. Donec venenatis id est sit amet laoreet.
                            Duis enim diam, sollicitudin sit amet egestas aliquam, laoreet in eros.</p>
                        </div>
                        <div className="containerIcons">
                            <div>
                                <img src={starblue} alt="" />
                                <h1>STAR BLUE</h1>
                                <p>Desafie adversÃ¡rios para ganhar stars</p>
                            </div>
                            <div>
                                <img src={starblue} alt="" />
                                <h1>STAR BLUE</h1>
                                <p>Desafie adversÃ¡rios para ganhar stars</p>
                            </div>
                            <div>
                                <img src={stargold} alt="Star Gold" />
                                <h1>STAR GOLD</h1>
                                <p>aposte e concorra a prÃªmios a cada partida</p>
                            </div>
                            <div>
                                <img src={converter} alt="Conversao" />
                                <h1>ConversÃ£o</h1>
                                <p>FaÃ§a trocas entre as Stars quando quiser</p>
                            </div>
                        </div>
                    </div>

                    <div className="containerStars">
                        <div className="contentHeader">
                            <img className="logoHeader" src={stargold} alt="logo"></img>
                            <div>
                                <h1 className="titleHeader">Star Gold</h1>
                                <p className="textHeader">Praesent velit ipsum, faucibus a ante vitae, aliquam viverra leo. Integer porttitor accumsan nisi vehicula convallis. Donec venenatis id est sit amet laoreet.</p>
                            </div>
                        </div>
                        <div className="containerValues">
                            {this.state.productsGold.map((product, index) => (
                                <div className="contentValue" key={index}>
                                    <p className="titleValue">{product.description}</p>
                                    <p className="cash">R$ {(product.price / 100)}</p>
                                    {this.props.user_data !== undefined ? (
                                        <button className="btnValue" onClick={() => this.redirectPagSeguro(product)}>Comprar</button>
                                    ) : (
                                            <button className="btnValue" onClick={() => this.setRedirect('/cadastro')}>Participe</button>
                                        )}
                                </div>
                            ))}
                        </div>
                    </div>

                    <div className="containerStars">
                        <div className="contentHeader">
                            <img className="logoHeader" src={starblue} alt="logo"></img>
                            <div>
                                <h1 className="titleHeader">Star Blue</h1>
                                <p className="textHeader">Praesent velit ipsum, faucibus a ante vitae, aliquam viverra leo. Integer porttitor accumsan nisi vehicula convallis. Donec venenatis id est sit amet laoreet.</p>
                            </div>
                        </div>
                        <div className="containerValues">
                            {this.state.productsBlue.map((product, index) => (
                                <div className="contentValue" key={index}>
                                    <p className="titleValue">{product.description}</p>
                                    <p className="cash"><span className="vezes">3x</span>R$ {(product.price / 100) / 3}</p>
                                    <p className="cashAvista">ou R${(product.price / 100)} Ã  vista</p>
                                    {this.props.user_data !== undefined ? (
                                        <button className="btnValue" onClick={() => this.redirectPagSeguro(product)}>Assinar</button>
                                    ) : (
                                            <button className="btnValue" onClick={() => this.setRedirect('/cadastro')}>Participe</button>
                                        )}
                                </div>
                            ))}
                        </div>
                    </div>

                    <div className="containerStars">
                        <div className="contentHeader">
                            <img className="logoHeader" src={converter} alt="logo"></img>
                            <div>
                                <h1 className="titleHeader">Converta Stars</h1>
                                <p className="textHeader">Praesent velit ipsum, faucibus a ante vitae, aliquam viverra leo. Integer porttitor accumsan nisi vehicula convallis. Donec venenatis id est sit amet laoreet.</p>
                                {this.props.user_data !== undefined ? (
                                    <button className="btnValueLoged" onClick={() => this.setRedirect('/')}>ir para meu perfil</button>
                                ) : (
                                        <div />
                                    )}
                            </div>
                        </div>
                    </div>

                    <div className="containerStars">
                        <div className="contentHeader">
                            <img className="logoHeader" src={resgatar} alt="logo"></img>
                            <div>
                                <h1 className="titleHeader">Resgate Stars</h1>
                                <p className="textHeader">Praesent velit ipsum, faucibus a ante vitae, aliquam viverra leo. Integer porttitor accumsan nisi vehicula convallis. Donec venenatis id est sit amet laoreet.</p>
                                {this.props.user_data !== undefined ? (
                                    <button className="btnValueLoged" onClick={() => this.setRedirect('/')}>Resgatar</button>
                                ) : (
                                        <div />
                                    )}
                            </div>
                        </div>
                    </div>
                </section>
            </React.Fragment>
        );
    }
}

export default SobreCompras;
