import React, { Component } from 'react';
// import { withFormik, Form, Field, ErrorMessage } from 'formik';
import { Link } from 'react-router-dom';
// import * as Yup from 'yup';

import logoPlaystars from './img/logo_final.svg';

import './styles.scss';



// const schema = Yup.object().shape({
//     email: Yup.string().email('E-mail ou senha incorretos!')
// });


// const enhanceWithFormik = withFormik({
//   mapPropsToValues: () => ({ email: '', senha: '' }),

//   isInitialValid: false,
//   validateOnChange: true,
//   validateOnBlur: true,
//   displayName: 'LoginComponent',
//   validationSchema: schema
// });

class LoginComponent extends Component {

    render() {
        return (
            <React.Fragment>
                <section className="tela-de-login">
                    <div className="login">
                        <img className="logo-principal" src={logoPlaystars} alt="Logo PlayStarts" />
                        <h2 className="titulo-logo">Acesse sua conta</h2>
                        <Form className="acesso">
                            <ErrorMessage className="error" component="span" name="email"/>
                            <fieldset>
                                <Field id="email" type="email" name="email" required/>
                                <label className="label" htmlFor="email">E-mail</label>
                            </fieldset>
                            <fieldset>
                                <Field id="senha" type="password" name="senha" onInput={this.on_input.bind(this)} required/>
                                <label className="label" htmlFor="senha">Senha</label>
                            </fieldset>
                            <button 
                                className="btnEntrar" 
                                type="submit" 
                                disabled={this.state.btn_disabled} 
                                style={{background: `${this.state.btn_color}` }}
                                >
                                    Entrar
                                </button>
                        </Form>
                        <div className="esqueci-senha">
                            <Link to="/esqsenha">Esqueci minha senha</Link>
                        </div>
                        <div className="sem-conta">
                            <Link to="/cadastro">Ainda n√£o tenho conta</Link>
                        </div>
                    </div>
                </section>
            </React.Fragment>
        )
    }
}

export default enhanceWithFormik(LoginComponent);
