import React from 'react';
import { Form, withFormik } from 'formik';
import * as Yup from 'yup';
import { Auth } from "aws-amplify";

import smartphone from './img/smartphone.png';
import '../CodConfirmacao/styles.scss';
import { withRouter } from 'react-router-dom';

import FormField from "../../";

const CodConfirm = (props) => (
    <div className="containerCodConfirm">
        <div className="lightBox">
            <div className="lightBoxContent">
                <img src={smartphone} alt="Icone Cel"/>
                <h3>Confirme seu número de telefone</h3>
                <p className="paragraph">Enviamos um código de confirmação por SMS para você. Por favor, digite-o abaixo.</p>
                <Form className="formConfirm">
                    <fieldset>
                        <FormField id="codConfirm" type="text" name="codigo" required />
                        <label className="label" htmlFor="codConfirm">Código de confirmação</label>
                    </fieldset>
                    <button className="btnDefault" type="submit" disabled={ props.isSubmitting }>Confirmar</button>
                    <button className="btnSecundary" type="submit">Reenviar Código</button>
                </Form>
            </div>
        </div>
    </div>   
);

const CodConfirmForm = withRouter(withFormik({
    mapPropsToValues({ codigo }) {
      return {
        codigo: codigo || ''
      }
    },
    validationSchema: Yup.object().shape({
      codigo: Yup.string()
        .required("Campo obrigatório")
        .length(6, 'Codigo inválido')
    }),
    handleSubmit({ codigo }, { props, setFieldError, setSubmitting }) {
      setSubmitting(true);
      console.log('Dados: ', {email: props.history.location.state.email, codigo: codigo});
      Auth.CodConfirm(props.history.location.state.email, codigo.toString())
        .then((result) => {
          setSubmitting(false);
          props.history.push('/');
        })
        .catch(error => {
          console.log('Erro confirmando assinatura: ', error);
          window.alert('Erro confirmando assinatura');
          setSubmitting(false);
        });
    }
  })(CodConfirm));

export default CodConfirmForm;
