import React, {useState, useEffect } from 'react';
import Select from '../../Ranking/Select/index';


import '../../Ranking/Filter/styles.scss';

function FilterHistoric(props) {


    let formattedStatusGeneric = (JSONValue) => {
        return JSONValue.map((StatusJSON) => {
            StatusJSON.value = StatusJSON.entity_id;
            StatusJSON.label = StatusJSON.name;
            return StatusJSON
        })
    }

    let FormattedJSONStatus = formattedStatusGeneric(this.props.duels);
    let currentStatus = !!status.length && FormattedJSONStatus.find((specificStatus) => specificStatus.name === status).games
    let FormattedJSONStatusDuels = !!status.length && formattedStatusGeneric(currentStatus)

    useEffect(() => {
        changeStatus(FormattedJSONStatusDuels[0])
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])


    function changeStatus(s) {
        console.log("Status :: ", s)
        status(s.name)
        props.setFilter("status", s)
    }

    return (
        <div className="containerFilter">
            <div className="containerSelects">
                <div className="containerSelect">
                    <Select
                        onChange={changeStatus}
                        width={'150px'}
                        placeholder={FormattedJSONStatusDuels.find(item => item.name === status) ? status : 'Status do Duelo'}
                        options={FormattedJSONStatusDuels}
                        classNames={'select-class'}
                        name="platform"
                    />
                </div>
            </div>
            {/* <button className="btnFilter" onClick={props.getFilter}>Filtrar</button> */}
        </div>
    )
}

export default FilterHistoric;
