import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import { Redirect } from "react-router-dom";
import { Auth, API } from "aws-amplify";
import { useDispatch } from "react-redux";
import { setUserData, setConsoles } from "../../../store/actions/user.action";
import config from "../../../config/constants";
import Loader from "../../elements/Loader";
import Menu from "../Header/Menu";
import Footer from "../Footer";

function PageContainer (props) {

  const [loading, setLoading] = useState(true);
  const [redirect, setRedirect] = useState(false);
  const [user_data, setUser_data] = useState(undefined);
  const [to, setTo] = useState("");
  const dispatch = useDispatch();

  useEffect(() => {
    if (!has_user_data()) {
      get_authenticated_user();
    }
    // eslint-disable-next-line
  },[])


  const get_user_data = (cognito_response) => {   
    const user_id = cognito_response.attributes.sub;

    if (props.user.data.entity_id) {
      setLoading(false);
      setUser_data({cognito_user: cognito_response, data: props.user})
      return;
    }

    API.get(config.APIS.PLAYERSTARS, `/player/${user_id}`, {})
      .then(response => {
        console.log("get user data bem sucedido. Response: ", response);
        dispatch(setUserData(response.data));
        setLoading(false);
        setUser_data({cognito_user: cognito_response, data: response.data})
      })
      .catch(error => {
        console.log("get user data falhou: ", error);
        if (error.response && error.response.status === 404) {
          setRedirect(true);
          setLoading(false);
          setTo("/seus-dados")
        } else {
          setRedirect(true);
          setLoading(false);
          setTo("/", {state: {message: error.message}});
          console.log("Erro obtendo dados o usuário: ", error.response);
        }
      });
      API.get(config.APIS.PLAYERSTARS, '/console/')
      .then(response => {
          console.log("ESSE E O RESPONSE", response.data)
          dispatch(setConsoles(response.data))
      })
      .catch(error => {
          console.log("ERRO NO GET: ")
      })
  }

  const get_authenticated_user = () => {
    Auth.currentAuthenticatedUser()
      .then(response => {
        console.log("get_authenticated_user, response:", response);
        if (is_register_view()) {
          setRedirect(true);
          setLoading(false);
          setTo("/");
          alert("Você já está no sistema");
        }
        if (!is_cadastro_view()) {
          get_user_data(response);
        } else {
          setLoading(false);
          setUserData({cognito_user: response});
        }
      })
      .catch(error => {
        if (is_register_view()) {
          setLoading(false);
          setRedirect(false);
        } else {
          if (!is_public_view()) {
            if (error === "not authenticated") {
              setRedirect(true);
              setLoading(false);
              setTo("/login");
            } else {
              setRedirect(true);
              setLoading(false);
              setTo("/", {state: {message: error.message}});
            }
          } else {
            setLoading(false);
          }
        }
      });
  }

  const is_cadastro_view = () => {
    return props.cadastro;
  }

  const is_register_view = () => {
    return props.register;
  }

  const is_public_view = () => {
    return props.public;
  }

  const has_user_data = () => {
    return props.user_data !== undefined;
  }


  const renderLoading = () => {
    return (
      <div className="Loading">
        <Loader />
      </div>
    );
  }

  const renderContent = () => {
    const children = React.Children.map(props.children, child =>
      React.cloneElement(child, {user_data: user_data })
    );

    return <>{children}</>;
  }


    const content = loading ? renderLoading() : renderContent();

    if (redirect) {
      return <Redirect to={to} />;
    }

    return (
      <>
        <Menu user_data={user_data} />
        {content}
        <Footer user_data={user_data} />
      </>
    );  
}

PageContainer.propTypes = {
  background: PropTypes.any,
  backgroundHeight: PropTypes.string,
  public: PropTypes.bool,
  cadastro: PropTypes.bool
};

export default connect(
  state => {
    const user = state.user;
    return {
      user
    };
  },
  {
    setUserData, setConsoles
  }
)(PageContainer);

