import React, { useState, useEffect } from 'react';
import config from '../../config/constants';
import { API } from "aws-amplify";

import './styles.scss';

import Table from './Table/index';
import Navigation from './Navigation/index';
import Filter from './Filter/index';

const initial_pagination = {
    per_page: 10,
    page: 1,
    total: 0
}


function Ranking() {

    const [pagination, setPagination] = useState(initial_pagination);
    const [ranking, setRanking] = useState([])
    const [filter, setFilter] = useState({})

    useEffect(()=>{
        console.log("FILTER ESSE:: ", filter)
        if(filter.platform && filter.game){
            getFilter(1)
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [filter])

    const getFilter = (page) => {
        const options = {response: true}
        console.log("FILTER :: ", filter)
        API.get(config.APIS.PLAYERSTARS, `/player/ranking/?console_id=${filter.platform.entity_id}&game_id=${filter.game.entity_id}&pagination_page=${page}&pagination_per_page=${10}`, options)
                .then(response => {
                    const range = response.headers["content-range"]
                    console.log("RANGE:::::::",range)
                    setRanking(response.data.data)
                    setPagination({
                        per_page: pagination.per_page,
                        page: page,
                        total: !range || range.split("/").pop() < 10 ? 10 : range.split("/").pop()
                    })
            })
            .catch(error => {
                console.log('::', error)
            })
    }

    const handleSetFilter = (key, item) => {
        console.log("SET FILTER :: ", filter, key, item)
        setFilter({
            ...filter,
            [key]: item
        })
    }

    const setPage = (page) => {
        getFilter(page)
    }

    return(
        <section className="containerRanking">
            <div className="contentRanking">
                <h1>Ranking</h1>
                <Filter filter={filter} setFilter={handleSetFilter}/>
                <Table ranking={ranking} />
                <Navigation pagination={pagination} setPage={(data) => setPage(data)}/>
            </div>
        </section>
    )
}

export default Ranking;
