import React, { Component } from "react";

import imgStarBlue from "../Converter/img/star-blue.png";
import imgStarGold from "../Converter/img/star-gold.png";
import seta from "../Converter/img/seta.svg";

import './styles.scss';

class Converter extends Component {

    state = {
        quantidade: ""
      };

      handleChange = e => {
        this.setState({ quantidade: e.target.value });
      };

      converterPremio = async () => {
        if (this.state.quantidade > this.props.saldoPremio) {
          toast.error("Saldo insuficiente.");
          return;
        }
        if (this.state.quantidade === 0 || !this.state.quantidade) {
          toast.warn("Informe um valor para ser convertido.");

          return;
        }
        const success = await this.props.handleConverterPremio(
          this.state.quantidade
        );
        if (success) {
          this.props.getUser();
          toast.success("Prêmio convertido com sucesso!");
          this.setState({ quantidade: "" });
        }
      };

    render() {
        return (

                <section className="containerConverter">       
                    <div className="contentConverter">                   
                        <div className="infoConverter"> 
                            <h1>Converter Stars</h1>
                            <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. 
                            Nam vestibulum, mi vel placerat auctor, tellus sapien congue ex, id ornare mi sem sit amet ex. </p>
                        </div>
                        <div className="converterStars">
                            <img className="img-star" src={imgStarGold} alt="Stars Gold"></img>
                            <fieldset>
                                <input className="inputStar" type="number" required/>
                                <label className="label" htmlFor="StarsBlue">Stars Gold</label>
                            </fieldset>
                            <img className="seta" src={seta} alt="conversao"></img>
                            <fieldset>
                                <input className="inputStar" type="number" required/>
                                <label className="label" htmlFor="StarsBlue">Stars Blue</label>
                            </fieldset>
                            <img className="img-star" src={imgStarBlue} alt="Stars Blue"></img>
                        </div>
                            <button className="btnDefault">Converter</button>                              
                    </div>
                </section>        
        )
    }
}

export default Converter
