import React, { Component, Fragment } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';

import MenuMobile from './MenuMobile';
import BtnMenu from './BtnMenu';

import logo from './img/logo-menu.png'
import iconLogin from './img/icon-login.png';
import iconBrasil from './img/icon-brasil.png';
import iconEua from './img/icon-eua.png';
import iconEspanha from './img/icon-espanha.png';

import './styles.scss';

class Menu extends Component {
    constructor(props) {
        super(props);
        this.state = {
            menuIsOpen: false,
            menuDropdown: false
        };
    };

    toggleMenu() {
        this.setState({ menuIsOpen: !this.state.menuIsOpen });
    }

    Dropdown = () =>{
        const { user } = this.props
        return (
            <div>
                <div className="headerUser">
                    <div className="nicknameUser">{user.nickname}</div>
                    <img className="avatarUser" src={user.profile_image} alt="Imagem de perfil" />

                    <div className="isdrop">
                        <div className="dropUser">
                            <div className="contentHeader">
                                <img className="avatar" src={user.profile_image} alt="Imagem de perfil" />
                                <div className="user">
                                    <span className="nameUser">{user.nickname}</span>
                                    <div className="emailUser">{user.email}</div>
                                </div>
                            </div>

                            <div className="linksUser">
                                <span className="border" />
                                <Link className="userLinks" to="/perfil">Meu Perfil</Link>
                                <Link className="userLinks" to="/converterstars">Converter Stars</Link>
                                <Link className="userLinks" to="/">Resgatar Star Gold</Link>
                                <Link className="userLinks" to="/historicocompras">Histórico de Compras</Link>
                                <Link className="userLinks" to="/historico">Histórico de Partidas</Link>
                                <Link to="/logout" className="userLinks">Sair</Link>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }

    render() {
        const { user } = this.props;
        return (
            <Fragment>
                <MenuMobile isLogged={this.props.user_data !== undefined} isOpen={this.state.menuIsOpen} />
                <BtnMenu
                    onClick={this.toggleMenu.bind(this)}
                    isOpen={this.state.menuIsOpen}
                />
                <div className="menu">

                    <nav className="menu-navegacao">
                         <img className="menu-logo" src={logo} alt="Logo PlayStarts" />
                        <div className="menu-navegacao-itens">
                            <Link className="menu-navegacao-item" to="/comojogar">Como Jogar</Link>
                            <Link className="menu-navegacao-item" to="/regras">Regras</Link>
                            <Link className="menu-navegacao-item" to="/compras">Compre Stars</Link>
                            <Link className="menu-navegacao-item" to="/ranking">Ranking</Link>
                            <Link className="menu-navegacao-item" to="/contato">Contato</Link>
                            <Link className="menu-navegacao-item" to="/cadastro">Participe</Link>
                        </div>
                        <div className="menu-login">
                            <div className="autenticacao">

                                {this.props.user_data !== undefined && user ? (
                                    this.Dropdown()
                                ) : (

                                        <div>
                                            <Link to="/login" className="autenticacao-login">Entra</Link>
                                            <img className="menu-login-icon-login" src={iconLogin} alt="Icone Login" />
                                        </div>
                                    )}
                                <div className="bandeiras">
                                    <img className="menu-login-icon-language" src={iconBrasil} alt="Icone Idioma" />
                                    <div className="idiomas">
                                        <p className="titulo-idiomas">Escolha um idioma</p>
                                        <ul>
                                            <li> <img className="menu-login-icon-language" src={iconBrasil} alt="Icone Idioma" />Português</li>
                                            <li> <img className="menu-login-icon-language" src={iconEspanha} alt="Icone Idioma" />Espanhol</li>
                                            <li> <img className="menu-login-icon-language" src={iconEua} alt="Icone Idioma" />Inglês</li>
                                        </ul>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </nav>
                </div>
            </Fragment>
        )
    }
}

const mapStateToProps = state => {
    return {
        user: state.user.data.user,
    }
}

export default connect(
    mapStateToProps, null
)(Menu)
