import React, { useState, useEffect } from 'react'
import { useSelector } from 'react-redux';
import { API } from "aws-amplify"
import config from '../../config/constants';

import imgStarBlue from "../Converter/img/star-blue.png";
import imgStarGold from "../Converter/img/star-gold.png";
import seta from "../Converter/img/seta.svg";
import './styles.scss';
import { useAlert } from 'react-alert';


const Converter = () => {

    const [quantidade, setQuantidade] = useState("");
    const [taxa, setTaxa] = useState()
    const data = useSelector(state => state.user.data); 
    const conversao = 4;
    const alert = useAlert()


   const handleChange = e => {
        setQuantidade(e.target.value);
      };

    const converterStars = () => {
        if (quantidade > data.golden_star_balance) {
            alert.show("Star Gold insuficiente");
          return;
        }

        if (quantidade === '0' || !quantidade)  {
            alert.error("Informe um valor valido para ser convertido.");
          return;
        }

        if (quantidade * taxa > quantidade) {
            alert.success("Stars convertidas com sucesso!");
            console.log(":::::::::", quantidade)
          setQuantidade("");
        }
    };

    useEffect(() => {
        API.get(config.APIS.PLAYERSTARS, '/convert-rate')
            .then(response => {
                console.log("ESSE É O RESPONSE  ", response.data[0].convert_rate)
                const taxa = response.data.convert_rate

            })
            .catch(error => {
                console.log("ERRO NO GET: ", error)
            })
    }, [ ])



    return (
        <React.Fragment>
            <section className="containerConverter">       
                <div className="contentConverter">                   
                    <div className="infoConverter"> 
                        <h1>Converter Stars</h1>
                        <p className="saldo">Seu saldo de Star Gold é de: {data.golden_star_balance}</p>
                        <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. 
                        Nam vestibulum, mi vel placerat auctor, tellus sapien congue ex, id ornare mi sem sit amet ex.</p>
                    </div>
                    <div className="converterStars">
                        <img className="img-star" src={imgStarGold} alt="Stars Gold"></img>
                        <fieldset>
                            <input 
                            id="StarsGold" 
                            onChange={handleChange}
                            value={quantidade}
                            type="number" 
                            required/>
                            <label className="label" htmlFor="StarsGold">Stars Gold</label>
                        </fieldset>
                        <img className="seta" src={seta} alt="conversao"></img>
                        <fieldset>
                            <input 
                            id="StarsBlue" 
                            onChange={handleChange}
                            value={quantidade && quantidade * conversao}         
                            type="number" 
                            required/>
                            <label className="label" htmlFor="StarsBlue">Stars Blue</label>
                        </fieldset>
                        <img className="img-star" src={imgStarBlue} alt="Stars Blue"></img>
                    </div>
                        <button onClick={converterStars} className="btnDefault">Converter</button>
                </div>
            </section>
        </React.Fragment>        
    )
}

export default Converter
