import React, { Component } from 'react';

import { Auth } from "aws-amplify";
import smartphone from '../../Cadastro/InicioDoCadastro/CodConfirmacao/img/smartphone.png';
import '../../Cadastro/InicioDoCadastro/CodConfirmacao/styles.scss';
import Loader from '../../Loader/index';
import { withRouter } from 'react-router-dom';


class confirmCodigo extends Component {

  state = {
    code: '',
    loading: false
  };

  forgotPasswordSubmit(ev, props) {
  ev.preventDefault()
  this.setState({loading: true})  
  Auth.forgotPasswordSubmit(props.email, this.state.code)
    .then(data => {
      console.log(data)
      this.props.history.push('/novasenha', { code: this.state.code })
    })
    .catch(err => console.log(err));
  }

  renderNew () {
    return (
      <React.Fragment>
        <div className="containerCodConfirm">
            <div className="lightBox">
                <div className="lightBoxContent">
                    <img src={smartphone} alt="Icone Cel"/>
                    <h3>Confirme seu número de telefone</h3>
                    <p className="paragraph">Enviamos um código de confirmação por SMS para você. Por favor, digite-o abaixo.</p>
                    <form className="formConfirm" onSubmit={this.forgotPasswordSubmit.bind(this)}>
                        <div className="inputInline">
                          <input id="codConfirm" type="text" name="codigo" onChange={(e) => {this.setState({code: e.target.value})} } label="Código de confirmação" required />
                        </div>
                        <button className="btnDefault" type="submit" required>Confirmar</button>
                        <button className="btnSecundary" type="submit">Reenviar Código</button> 
                    </form>
                </div>
            </div>
        </div>
      </React.Fragment>
    )
  }
    renderLoading() {
      return (
      <div>
          <Loader />
      </div>
      )
  }

  render() {
      const content = (this.state.loading) ? (this.renderLoading()) : (this.renderEsq())
      return (
          <span>
          {content}
          </span>
      )
  }

};

export default withRouter(confirmCodigo);
