import React, { Component } from 'react';
import {Link, withRouter} from 'react-router-dom';
import {Auth} from 'aws-amplify';
import logoPlaystars from './img/logo_final.svg';
import './styles.scss';


class Login extends Component {
    state = {
        email: '',
        senha: '',
        loading: false,
        btn_disabled: true,
        btn_color: '#BBB',
    };

    on_input (i) {
        const target = i.target;
        const habilitar = target.value.length;

        if (this.state.email.length !== 0 ) {
            this.setState({ btn_disabled: false});
            this.setState({ btn_color: '#000'})
        } else {
            this.setState({ btn_disabled: true})
            this.setState({ btn_color: '#BBB'})
        }
    }

    handleChange(ev) {
        const newState = {[ev.target.name]: ev.target.value}
        this.setState(newState)
      }

      handleSubmit(ev) {
        ev.preventDefault()
        this.setState({loading: true})
        Auth.signIn(this.state.email, this.state.senha)
          .then(response => {
            console.log('Login com sucesso. Response: ', response)
            console.log('Props: ', this.props)
            this.props.history.push('/')
          })
          .catch(error => {
            console.log('Erro fazendo login: ', error)
            if(error.code === 'UserNotConfirmedException') {
              this.props.history.push('/codconfirmacao', { email: this.state.email });
            }
            else {
              this.props.history.push('/error', { message: error.message });
            }
          })
      }

    renderForm() {
        return (
            <React.Fragment>
                <section className="tela-de-login">
                    <div className="login">
                        <img className="logo-principal" src={logoPlaystars} alt="Logo PlayStarts" />
                        <h2 className="titulo-logo">Acesse sua conta</h2>
                        <form className="acesso" onSubmit={this.handleSubmit.bind(this)}>
                            <fieldset>
                                <input id="email" 
                                    type="email" 
                                    name="email" 
                                    required 
                                    onChange={this.handleChange.bind(this)}
                                />
                                <label className="label" htmlFor="email">E-mail</label>
                            </fieldset>
                            <fieldset>
                                <input id="senha" 
                                    type="password" 
                                    name="senha" 
                                    onInput={this.on_input.bind(this)} 
                                    required 
                                    onChange={this.handleChange.bind(this)}
                                />
                                <label className="label" htmlFor="senha">Senha</label>
                            </fieldset>
                            <input 
                                className="btnEntrar" 
                                type="submit" 
                                disabled={this.state.btn_disabled} 
                                style={{background: `${this.state.btn_color}` }} 
                                value="Entrar"
                            />           
                        </form>
                        <div className="esqueci-senha">
                            <Link to="/esqsenha">Esqueci minha senha</Link>
                        </div>
                        <div className="sem-conta">
                            <Link to="/cadastro">Ainda não tenho conta</Link>
                        </div>
                    </div>
                </section>
            </React.Fragment>
        )
    }

    renderLoading() {
        return (
        <div className="tela-de-login">
            <p>Carregando...</p>
        </div>
        )
    }

  render() {
    const content = (this.state.loading) ? (this.renderLoading()) : (this.renderForm())
    return (
        <span>
        {content}
        </span>
    )
  }
}

const LoginForm = withRouter(Login)

export default LoginForm;
