import React, { Component } from 'react'
import config from '../../config/constants';
import { API } from "aws-amplify"
import './styles.scss';

class Contato extends Component {

    state = {
        nome: '',
        email: '',
        assunto: '',
        mensagem: '',
        btn_disabled: true,
        btn_color: '#BBB',
    }


    changeHandler = (e) => {
        this.setState({ [e.target.name]: e.target.value })
    }

    on_input (e) {
        this.changeHandler(e)
        if (this.state.nome.length >= 3 && this.state.email.length >= 3 ) {
            this.setState({ btn_disabled: false});
            this.setState({ btn_color: '#000'})
        } else {
            this.setState({ btn_disabled: true})
            this.setState({ btn_color: '#BBB'})
        }
    }

    sendContact() {
        const options = {
            body: {
                "subject": this.state.assunto,
                "contact_message": this.state.mensagem,
                "data": []
            }
        }

        API.post(config.APIS.PLAYERSTARS, '/contact-email/send', options)
            .then(response => {
                console.log('RESPONSE: ', response)
                this.setState({
                    nome: '',
                    email: '',
                    assunto: '',
                    mensagem: ''

                })
            })
            .catch(error => {
                console.log("ERROR: ", error)
                window.alert('Erro fazendo post do contato do usu√°rio.');
            })
    }

    render() {
        const { nome, assunto, email, mensagem } = this.state
        return (
            <React.Fragment>
                <section className="containerContato">
                    <div className="contentContato">
                        <h1 className="titleContato">Contato</h1>
                        <p className="paragrafyContato">Mollis nisl. Maecenas pulvinar turpis at faucibus posuere. Maecenas ut nibh non mauris dictum.</p>
                        <form className="formContato" onSubmit={this.submitHandler}>
                            <fieldset>
                                <input id="nome" type="text" name="nome" value={nome} onChange={this.changeHandler} required />
                                <label className="label" htmlFor="nome">Nome</label>
                            </fieldset>
                            <fieldset>
                                <input id="email" type="email" name="email" value={email} onChange={this.changeHandler} required />
                                <label className="label" htmlFor="email">E-mail</label>
                            </fieldset>
                            <fieldset>
                                <input id="assunto" type="text" name="assunto" value={assunto} onChange={this.changeHandler} required />
                                <label className="label" htmlFor="assunto">Assunto</label>
                            </fieldset>
                            <fieldset>
                                <textarea id="mensagem" name="mensagem" value={mensagem} onChange={this.changeHandler} onInput={this.on_input.bind(this)} required></textarea>
                                <label className="label" htmlFor="mensagem">Mensagem</label>
                            </fieldset>
                            <button className="btnEnviar" type="submit" onClick={this.sendContact.bind(this)} disabled={this.state.btn_disabled} 
                                style={{background: `${this.state.btn_color}` }}>Enviar</button>
                        </form>
                    </div>
                </section>
            </React.Fragment>
        )
    }
}

export default Contato;
