import React, { useState, useEffect } from 'react';
import Select from '../../Select';
import { API } from "aws-amplify"
import config from '../../../config/constants';

function FilterHistoric(props) {

    const [statusDuel, setStatusDuel] = useState('');
    const [optionsDuel, setOptionsDuel] = useState('');

    useEffect(() => {
        getStatus()
        // eslint-disable-next-line
    }, [])

    let formattedStatusGeneric = (JSONValue) => {
        return JSONValue.map((consoleJSON) => {
            consoleJSON.value = consoleJSON.entity_id;
            consoleJSON.label = consoleJSON.name;
            return consoleJSON
        })
    }

    const getStatus = () => {
        API.get(config.APIS.PLAYERSTARS, '/duel/get-my-duels')
            .then(response => {
                console.log("ESSE É O RESPONSE DO STATUS: ", response.data)
                setOptionsDuel(response.data.map((item , index) => {   
                    return item
                }))
            })
            .catch(error => {
                console.log("ERRO NO GET: ", error)
            })
    }

    function changeStatusDuel(s) {  
        setStatusDuel(s)
        props.setFilter(s)
    }

    return (
        <div className="containerFilter">
            <div className="containerSelects">
                <div className="containerSelect">
                    <Select
                        onChange={changeStatusDuel}
                        width={'150px'}
                        placeholder={statusDuel ? statusDuel : "Status"}
                        options={optionsDuel}
                        classNames={'select-class'}
                        name="statusDuel"
                    />
                </div>
            </div>
        </div>
    )
}

export default FilterHistoric;
