import React from 'react';
import { Form, withFormik } from 'formik';
import * as Yup from 'yup';
import { Auth } from "aws-amplify";

import smartphone from '../../Cadastro/InicioDoCadastro/CodConfirmacao/img/smartphone.png';
import '../../Cadastro/InicioDoCadastro/CodConfirmacao/styles.scss';
import { withRouter } from 'react-router-dom';

const confirmCodigo = () => {

  return (
        <React.Fragment>
            <div className="containerCodConfirm">
                <div className="lightBox">
                    <div className="lightBoxContent">
                        <img src={smartphone} alt="Icone Cel"/>
                        <h3>Confirme seu número de telefone</h3>
                        <p className="paragraph">Enviamos um código de confirmação por SMS para você. Por favor, digite-o abaixo.</p>
                        <form className="formConfirm">
                            <div className="inputInline">
                                <input id="codConfirm" type="text" name="codigo" label="Código de confirmação" required/>
                            </div>
                            <button className="btnDefault" type="submit" required>Confirmar</button>
                            <button className="btnSecundary" type="submit">Reenviar Código</button> 
                        </form>
                    </div>
                </div>
            </div>
        </React.Fragment>
    )
};

const ConfirmCodigoForm = withRouter(withFormik({
    mapPropsToValues({ codigo }) {
      return {
        codigo: codigo || ''
      }
    },
    validationSchema: Yup.object().shape({
      codigo: Yup.string()
        .required("Campo obrigatório")
        .length(6, 'Codigo inválido')
    }),
    handleSubmit({ codigo }, { props, setFieldError, setSubmitting }) {
      setSubmitting(true);
      console.log('Dados: ', {email: props.history.location.state.email, codigo: codigo});
      Auth.confirmSignUp(props.history.location.state.email, codigo.toString()).then((result) => {
          setSubmitting(false);
          props.history.push('/dadosuser', {initCadastro: props.history.location.state.email, codigoconfimacao: codigo});
          console.log("Minhas props no codigo de confirmação são", props.history.location)
        })
        .catch(error => {
          console.log('Erro confirmando assinatura: ', error);
          window.alert('Codigo de confirmação invalido');
          setSubmitting(false);
        });
    }
  })(confirmCodigo));

export default ConfirmCodigoForm;
