import React from "react";
import { withRouter, useHistory } from "react-router-dom";
import { useAlert } from 'react-alert';
import { Auth } from "aws-amplify";
import { Form, withFormik } from "formik";
import * as Yup from "yup";
import PageContainer from "../../components/features/PageContainer";
import Email from "../../assets/icons/email.png";
import FormField from "../../components/elements/FormField";

import "./styles.scss";

const ConfirmSignUp = (props) => {
  const alert = useAlert();
  const history = useHistory();
  const resendCode = ev => {
    ev.preventDefault()
    Auth.forgotPassword(props.history.location.state.email)
    .then(data => {
        alert.success('Codigo enviado');
    })
    .catch(err => {
        console.log('ERROR', err);
        const defaultMessage = "Erro ao reenviar o código, tente novamento mais tarde";
        const selectMessageError = {
          InvalidParameterException: "Não é possível reenviar o código, pois não há e-mail registrado / verificado.",
          LimitExceededException: "Número de tentativas excedida, tente depois de algum tempo.",
        }
        return alert.error(selectMessageError[err.code] || defaultMessage);
    });
  }

  return (
    <>
      <PageContainer register={true}>
        <div className="containerCodConfirm">
          <div className="lightBox">
            <div className="lightBoxContent">
              <img src={Email} alt="Icone email" />
              <h3>Confirme seu e-mail</h3>
              <p className="paragraph">
                Enviamos um código de confirmação por e-mail para você. Por favor, digite-o abaixo.
              </p>
              <Form className="formConfirm">
                <div className="inputInline">
                  <FormField
                    id="codConfirm"
                    type="text"
                    name="codigo"
                    label="Código de confirmação"
                  />
                </div>
                <button
                  className="btnDefault"
                  type="submit"
                  style={atv()}
                  disabled={!props.isValid}
                  required={props.isSubmitting}
                >
                  Confirmar
                </button>
                <button 
                className="btnSecundary" 
                type="submit"
                onClick={resendCode}
                >
                  Reenviar Código
                </button>
              </Form>
            </div>
          </div>
        </div>
      </PageContainer>
    </>
  );
  function atv() {
    const x = props.isValid;
    if (x === true) {
      return { background: `#000` };
    } else {
      return { background: `#BBB` };
    }
  }
};

const ConfirmSignupForm = withRouter(
  withFormik({
    mapPropsToValues({ codigo }) {
      return {
        codigo: codigo || "",
      };
    },
    validationSchema: Yup.object().shape({
      codigo: Yup.string()
        .required("Campo obrigatório")
        .length(6, "Codigo inválido"),
    }),
    handleSubmit({ codigo }, { props, setFieldError, setSubmitting }) {
      setSubmitting(true);

      if(props.location.state.pathname === "/esqueci-senha") {
        props.history.push("/nova-senha", {email: props.history.location.state.email, code: codigo})
      } else {
      Auth.confirmSignUp(props.history.location.state.email, codigo.toString())
        .then((result) => {
          setSubmitting(false);
          Auth.signIn(
            history.location.state.email,
            history.location.state.senha
          )
            .then((response) => {
              alert.success("E-mail validado com sucesso!");
              props.history.push("/seus-dados", {
                initCadastro: props.history.location.state.email,
                senha: props.history.location.state.senha,
                codigoconfimacao: codigo,
              });
              console.log(
                "Minhas props no codigo de confirmação são",
                props.history.location
              );
            })
            .catch((error) => {
              console.log("Erro ", error);
              const messageDefault = "Ocorreu um erro inesperado.";

              const selectErrorMessage = {
                NotAuthorizedException: "E-mail ou senha incorretos.",
                UserNotFoundException: "O usuário não foi encontrado.",
                PasswordResetRequiredException:
                  'A sua senha foi resetada. Crie uma nova em "esqueci minha senha".',
                InvalidPasswordException:
                  "A senha deve conter ao mínimo 8 dígitos.",
              };

              return alert.error(selectErrorMessage[error.code] || messageDefault);
            });
        })
        .catch((error) => {
          console.log("Erro confirmando assinatura: ", error);
          alert.error("Codigo de confirmação invalido");
          setSubmitting(false);
        });
      }
    },
  })(ConfirmSignUp)
);

export default ConfirmSignupForm;
