import React, { useState } from 'react';
// import { useAlert } from 'react-alert';

import newsenha from './img/nova-senha.png';

import './styles.scss';



const NovaSenha = () => {

    const [newPassword, setNewPassword] = useState("");
    const [confnewpassword, setConfnewpassword] = useState("");
    const [btn_disabled, setBtn_disabled] = useState(true);
    const [btn_color, setBtn_color] = useState('#BBB');
    // const alert = useAlert()   

    const handleChange = (ev) => {
        return (
        setNewPassword({[ev.target.name]: ev.target.value}),
        setConfnewpassword({[ev.target.name]: ev.target.value})
        )
      }



   const on_input = (ev) => {
        handleChange(ev)           
        if (newPassword.length > 5 && confnewpassword.length > 5) {
            setBtn_disabled({ btn_disabled: false});                
            setBtn_color({ btn_color: '#000'})            
        } else {                
            setBtn_disabled({ btn_disabled: true})                
            setBtn_color({ btn_color: '#BBB'})           
        }                  
    }

    return (
        <div className="containerNovaSenha">
            <div className="lightBox">
                <div className="lightBoxContent">
                    <img src={newsenha} alt="Icone Cel"/>
                    <h3>Criar nova senha</h3>
                    <p className="paragraph">Agora é só redefnir sua senha!</p>
                    <form className="formConfirm">
                        <fieldset>
                            <input id="newPassword" type="password" name="newPassword" onInput={on_input} onChange={handleChange} required />
                            <label className="label" htmlFor="newPassword">Nova Senha</label>
                        </fieldset>
                        <fieldset>
                            <input id="confNewPassword" type="password" name="confnewpassword" onInput={on_input} onChange={handleChange} required />
                            <label className="label" htmlFor="confnewpassword">Repetir nova senha</label>
                        </fieldset>
                        <button className="btnDefault" 
                        disabled={btn_disabled} 
                        style={{background: `${btn_color}` }} 
                        type="submit">Confirmar</button>
                    </form>
                </div>
            </div>
        </div>
    )   
}

export default NovaSenha;
