import React, { Component } from "react";
import PropTypes from "prop-types";
import { Redirect } from "react-router-dom";
import { Auth, API } from "aws-amplify";
import { connect } from "react-redux";
import { setUserData } from "../../store/actions/user.action";
import config from "../../config/constants";
import Loader from "../../components/Loader/index";
import Menu from "../../components/Header/Menu";
import Footer from "../../components/Footer";

class View extends Component {
  state = {
    loading: true,
    redirect: false,
    user_data: undefined
  };

  redirect_to_error(error_message) {
    this.setState({
      redirect: true,
      loading: false,
      to: {
        pathname: "/",
        state: {
          message: error_message
        }
      }
    });
  }

  redirect_to_login() {
    this.setState({
      redirect: true,
      loading: false,
      to: {
        pathname: "/login"
      }
    });
  }

  redirect_to_cadastro() {
    this.setState({
      loading: false,
      redirect: true,
      to: { pathname: "/dadosuser", state: { initCadastro: true } }
    });
  }

  redirect_to_home() {
    this.setState({
      redirect: true,
      loading: false,
      to: { pathname: "/" }
    });
  }

  get_user_data(cognito_response) {
    const user_id = cognito_response.attributes.sub;
    console.log("USER_ID:", user_id);
    console.log("COGNITO RESPONSE: ", cognito_response);
    if (this.props.user.data.entity_id) {
      this.setState({
        loading: false,
        user_data: {
          cognito_user: cognito_response,
          data: this.props.user
        }
      });
      return;
    }
    API.get(config.APIS.PLAYERSTARS, `/player/${user_id}`, {})
      .then(response => {
        console.log("get user data bem sucedido. Response: ", response);
        this.props.setUserData(response.data);
        this.setState({
          loading: false,
          user_data: {
            cognito_user: cognito_response,
            data: response.data
          }
        });
      })
      .catch(error => {
        console.log("get user data falhou: ", error);
        if (error.response && error.response.status === 404) {
          this.redirect_to_cadastro();
        } else {
          this.redirect_to_error("Erro obtendo dados do usuário"); //error.response.message)
          console.log("Erro obtendo dados o usuário: ", error.response);
        }
      });
  }
  get_authenticated_user() {
    Auth.currentAuthenticatedUser()
      .then(response => {
        console.log("get_authenticated_user, response:", response);
        if (this.is_register_view()) {
          this.redirect_to_home();
          alert("Você já está no sistema");
        }
        if (!this.is_cadastro_view()) {
          this.get_user_data(response);
        } else {
          this.setState({
            loading: false,
            user_data: {
              cognito_user: response
            }
          });
        }
      })
      .catch(error => {
        if (this.is_register_view()) {
          this.setState({ loading: false, redirect: false });
        } else {
          if (!this.is_public_view()) {
            if (error === "not authenticated") {
              this.redirect_to_login();
            } else {
              this.redirect_to_error(error);
            }
          } else {
            this.setState({ loading: false });
          }
        }
      });
  }

  is_cadastro_view() {
    return this.props.cadastro;
  }

  is_register_view() {
    return this.props.register;
  }

  is_public_view() {
    return this.props.public;
  }

  has_user_data() {
    return this.props.user_data !== undefined;
  }

  componentDidMount() {
    window.scrollTo(0, 0);
    if (!this.has_user_data()) {
      this.get_authenticated_user();
    }
  }

  // noinspection JSMethodCanBeStatic
  renderLoading() {
    return (
      <div className="Loading">
        <Loader />
      </div>
    );
  }

  renderContent() {
    const children = React.Children.map(this.props.children, child =>
      React.cloneElement(child, { user_data: this.state.user_data })
    );

    return <React.Fragment>{children}</React.Fragment>;
  }

  render() {
    console.log(":::::::::::::", this.props);
    if (this.state.redirect) {
      return <Redirect to={this.state.to} />;
    }

    const content = this.state.loading
      ? this.renderLoading()
      : this.renderContent();
    return (
      <React.Fragment>
        <Menu user_data={this.state.user_data} />
        {content}
        <Footer user_data={this.state.user_data} />
      </React.Fragment>
    );
  }
}

View.propTypes = {
  background: PropTypes.any,
  backgroundHeight: PropTypes.string,
  public: PropTypes.bool,
  cadastro: PropTypes.bool
};

export default connect(
  state => {
    const user = state.user;
    return {
      user
    };
  },
  {
    setUserData
  }
)(View);
