import React, { useState, useEffect } from 'react';
import { Redirect, Link } from 'react-router-dom';
import { API } from "aws-amplify";
import moment from 'moment';
import config from '../../config/constants';
import PageContainer from "../../components/features/PageContainer";
import Loader from '../../components/elements/Loader';

import starRed from '../../assets/images/star-red-color.png';
import starGold from '../../assets/images/star-gold-color.png';
import editar from '../../assets/images/editar-perfil-profile.png';

import './styles.scss';

function RenderLoading() {
    return (
        <div className="Loading">
            <Loader />
        </div>
    )
}

function PerfilUsuario () {

    const [userr, setUserr] = useState({});
    const [playerr, setPlayerr] = useState({});
    const [consoless, setConsoless] = useState([]);
    const [redirectt, setRedirectt] = useState(false);
    const [isLoadingg, setIsLoadingg] = useState(true);


    useEffect(() => {
        getDadosProfile()
    },[])

   const getDadosProfile = () => {
        API.get(config.APIS.PLAYERSTARS, '/player/get-my-profile')
            .then(response => {
                console.log("RESULTADO DO GET: ", response.data);
                setUserr(response.data.player.user);
                setPlayerr(response.data.player);
                setConsoless(response.data.consoles);
                setIsLoadingg(false);

            })
            .catch(error => {
                console.log("ERRO NO GET: ", error)
            })
    }


    if (isLoadingg) {
        return <RenderLoading />
    }

    return (
        <>
            <PageContainer public={false}>
            {isLoadingg && <RenderLoading />}
            {redirectt ? <Redirect to='/editar-perfil' /> : <div></div>}
            {console.log("::::REDIRECT::", redirectt)}
            <section className="containerProfile">
                <div className="containerDadosUser">
                    <div className="headerDadosUser">
                        <div className="containerAvatarUser">
                            <div className="containerAvatar">
                                <div className="avatar" style={{ backgroundImage: `url(${userr.profile_image}?${Date.now()})` }}></div>
                            </div>

                            <div className="contentUser">
                                <h3 className="nickname">{userr.nickname}</h3>
                                <span className="emailUser">{userr.email}</span>
                            </div>
                        </div>
                        <div className="containerIconEditProfile" onClick={() => setRedirectt(true)} >
                            <img className="iconEdit" src={editar} alt="Editar perfil" />
                            <span className="editProfile">Editar</span>
                        </div>
                    </div>
                    <div className="containerConquests">
                        <div className="contentConquest">
                            <img className="iconConquest" src={starGold} alt="StarGold" />
                            <span className="descriptionConquest" alt="StarRed">Gold Star · {playerr.golden_star_balance}</span>
                        </div>
                        <div className="contentConquest">
                            <img className="iconConquest" src={starRed} alt="StarRed" />
                            <span className="descriptionConquest">Red Star · {playerr.red_star_balance}</span>
                        </div>
                    </div>

                    <div className="containerDadosAddress">
                        <div className="contentDados">
                            <h3 className="titleDadosAddress">Dados pessoais</h3>
                            <p className="descriptionDados">{userr.name}</p>
                            <div className="containerNasCpfTel">
                                <div className="contentNasCpfTel">
                                    <span className="titleNasCpfTel">Nascimento</span>
                                    <span className="descriptionNasCpfTel">{moment(userr.date_birth).format('DD/MM/YYYY')}</span>
                                </div>
                                <div className="contentNasCpfTel">
                                    <span className="titleNasCpfTel">CPF</span>
                                    <span className="descriptionNasCpfTel">{userr.cpf}</span>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="border"></div>
                <div className="containerPlatform">
                    <h2 className="titlePlatform">Minhas Plataformas</h2>
                    <div className="containerIconEditConsole">
                        <img className="iconEdit" src={editar} alt="Editar console" />
                        <Link to="/editar-consoles"><span className="editConsole">Editar</span></Link>
                    </div>
                    <div className="consoleName">
                        {consoless.map((item, index) => (<div key={index} className="consoleTagname">{item.name} · {item.tag_name}</div>))}                           
                    </div>
                </div>
            </section>
            </PageContainer>
        </>
    )

}

export default PerfilUsuario;
