import React, { useState, useEffect } from 'react';
import { API, Auth } from "aws-amplify"
import config from "../../../config/constants"
import { useSelector } from 'react-redux';

import './styles.scss';

function EditarConsoles(props) {
    const currentConsoles = useSelector(state => state.user.data.consoles);
    const [userConsole, setUserConsole] = useState('');

    useEffect(() => {
        API.get(config.APIS.PLAYERSTARS, '/console/')
            .then(response => {
                console.log("ESSE E O RESPONSE", response.data)
                setUserConsole(response.data);
            })
            .catch(error => {
                console.log("ERRO NO GET: ")
            })
            // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])

    const findConsole = (consoles, platformName) => {
        const _console = consoles.find(item => item.name === platformName)
        return _console ? _console.tag_name : ''   
    }

    const handleSubmit = (ev) => {
        ev.preventDefault()
        const listConsoles = Object.keys(form).map(key=>{
            return {
                entity_id: form[key].entity_id,
                tag_name: form[key].tag_name
            }
        })


            const options = {
                body: {
                    consoles: listConsoles
                }
            }

            API.put(config.APIS.PLAYERSTARS, '/console', options)
                .then(response => {
                    props.history.push('/perfil');
                })
                .catch(error => {
                    console.log('Esse é o retorno das options', options);
                    window.alert('Erro fazendo post dos dados do usuário.');
                })

    }

  return (
    <section className="containerEditConsole">
        <div className="contentEditConsole">
            <h2 className="titleEditConsole">Editar plataformas</h2>
            <p className="subTitleEditConsole">Quais plataformas você usa?</p>
            <form onSubmit={handleSubmit} className="dadosConsole">
                {userConsole.length > 0 ?
                    userConsole.map((platform, index) => (
                        <fieldset key={index}>
                            <input 
                                id={platform.name} 
                                type="text" 
                                name={platform.name}
                                onChange={handleChangeInput} 
                                defaultValue={findConsole(currentConsoles, platform.name)}
                                // required
                                />
                            <label className="label" htmlFor={platform.name}>Tagname do {platform.name}</label>
                        </fieldset>
                    )) : <div></div>
                }
                <div className="containerBtn">
                    <button className="btnCancel" type="button">Cancelar</button>
                    <button className="btnSave" type="submit" >Salvar alterações</button>
                </div>
            </form>
        </div>
    </section>
  );
}

export default EditarConsoles;
