import React, { useState, useEffect } from 'react';
import { withRouter, Redirect } from "react-router-dom";
import { useSelector, useDispatch } from 'react-redux';
import { withFormik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import config from '../../../config/constants';
import { API } from "aws-amplify";
import BrDate from '../../FormikBrDate/formik_br_date';
import MaskedInput from 'react-text-mask';
import {setUserImage} from "../../../store/actions/user.action"

import iconremove from '../img/icon-remove.png';
import iconedit from '../img/editar-perfil.png';



function EditarPerfil(props) {
    const dispatch = useDispatch()
    const [thumbnailFile, setThumbnailFile] = useState("");
    const [preview, setPreview] = useState("")
    const [user, setUser] = useState([]);
    const userOld = useSelector(state => state.user.data.user);
    const avataruser = "https://playerstars-dev-photos.s3.amazonaws.com/WhatsApp+Image+2020-01-14+at+18.07.32.jpeg";
    const profile_image = userOld ? userOld.profile_image : avataruser;

    useEffect(() => {

        setPreview(thumbnailFile ? URL.createObjectURL(thumbnailFile) : profile_image);
    }, [thumbnailFile, profile_image])

    useEffect(() => {
        API.get(config.APIS.PLAYERSTARS, '/player/get-my-profile')
            .then(response => {
                setUser(response.data.player.user);
            })
            .catch(error => {
                console.log("ERRO NO GET: ", error)
            })
    }, []);

    useEffect(() => {
        props.setValues(userOld)
         // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [userOld])


    const removePreview = () => {
        setThumbnailFile("")
        dispatch(setUserImage(avataruser))
    }

    return (
        <>
            <section className="containerEditProfile">
                <div className="contentEditProfile">
                    <h2 className="titleEditProfile">Editar Perfil</h2>
                    <div className="containerEditAvatar">
                        <div className="containerAvatar">
                            <div id="thumbnail"
                                style={{ backgroundImage: `url(${preview}?${Date.now()})`}}
                                className="thumbnail"
                            ></div>
                        </div>
                        <div className="containerEditRemove">
                            <label className="containerPutAvatar" onClick={removePreview}>
                                <img className="iconEdit" src={iconremove} alt="Icon remover" />
                                <span className="textEdit">Excluir</span>
                            </label>
                        </div>
                    </div>
                    {EditForm(user)}
                </div>
            </section>
        </>
    )
}

const estados = ["AC", "AL", "AP", "AM", "BA", "CE", "DF", "ES", "GO", "MA", "MT", "MS",
    "MG", "PA", "PB", "PR", "PE", "PI", "RJ", "RN", "RS", "RO", "RR", "SC",
    "SP", "SE", "TO"];

function EditForm() {
    const [redirect, setRedirect] = useState(false)
    return (
        <>
            {redirect && <Redirect to='/' />}
            <Form className="dadosPessoais">
                <div className="blocoDadosPessoais">
                    <h3 className="titleH3">Dados pessoais</h3>
                    <ErrorMessage className="error" component="span" name="nome" />
                    <fieldset>
                        <Field id="nome" type="text" name="name" required />
                        <label className="label" htmlFor="nome" >Nome completo</label>
                    </fieldset>
                    <ErrorMessage className="error" component="span" name="nascimento" />
                    <ErrorMessage className="error" component="span" name="nickname" />
                    <div className="doisCampos">
                        <fieldset>
                            <Field name="date_birth" render={({ field }) => {
                                return <MaskedInput mask={[/[0-9]/, /\d/, '/', /\d/, /\d/, '/', /\d/, /\d/, /\d/, /\d/]}
                                    {...field}
                                    id="nascimento"
                                    type="text"
                                    required />
                            }} />
                            <label className="label" htmlFor="nascimento">Nascimento</label>
                        </fieldset>
                        <fieldset>
                            <Field id="nickname" type="text" name="nickname" required />
                            <label className="label" htmlFor="nickname" >Nickname</label>
                        </fieldset>
                    </div>
                </div>
                <div className="blocoEnderecoCompleto">
                    <h3 className="titleH3">Endereço completo</h3>
                    <ErrorMessage className="error" component="span" name="uf" />
                    <ErrorMessage className="error" component="span" name="cidade" />
                    <div className="doisCampos">
                        <fieldset>
                            <Field id="uf" name="state" required component="select">
                                <option value="">UF</option>
                                {estados.map((estado, index) => <option value={estado} key={index}>{estado}</option>)}
                            </Field>
                            <label className="label" htmlFor="uf" />
                        </fieldset>
                        <fieldset>
                            <Field id="cidade" type="text" name="city" required />
                            <label className="label" htmlFor="cidade">Cidade</label>
                        </fieldset>
                    </div>
                    <ErrorMessage className="error" component="span" name="bairro" />
                    <fieldset>
                        <Field id="bairro" type="text" name="neighborhood" required />
                        <label className="label" htmlFor="bairro">Bairro</label>
                    </fieldset>
                    <ErrorMessage className="error" component="span" name="rua" />
                    <fieldset>
                        <Field id="rua" type="text" name="street" required />
                        <label className="label" htmlFor="rua">Rua</label>
                    </fieldset>
                    <ErrorMessage className="error" component="span" name="numero" />

                    <ErrorMessage className="error" component="span" name="cep" />
                    <div className="doisCampos">
                        <fieldset>
                            <Field id="numero" type="text" name="street_number" required />
                            <label className="label" htmlFor="numero">Número</label>
                        </fieldset>
                        <fieldset>
                            <Field name="postal_code" render={({ field }) => {
                                return <MaskedInput mask={[/[0-9]/, /\d/, /\d/, /\d/, /\d/, '-', /\d/, /\d/, /\d/]}
                                    {...field}
                                    id="cep"
                                    type="text"
                                    required />
                            }} />
                            <label className="label" htmlFor="cep">CEP</label>
                        </fieldset>
                    </div>
                    <fieldset>
                        <Field id="complemento" type="text" name="street_complement"  />
                        <label className="label" htmlFor="complemento">Complemento - <i>opcional</i></label>
                    </fieldset>
                    <div className="containerBtn">
                        <button className="btnCancel" type="button" onClick={() => setRedirect(true)}>Cancelar</button>
                        <button className="btnSave" type="submit" >Salvar alterações</button>
                    </div>
                </div>
            </Form>
        </>
    )
}

const SaveEdit = withRouter(withFormik({
    mapPropsToValues({
        name,
        date_birth,
        nickname,
        state,
        city,
        neighborhood,
        street,
        street_number,
        postal_code,
        street_complement,
        profile_image
    }) {
        return {
            name: name || '',
            date_birth: date_birth || '',
            nickname: nickname || '',
            state: state || '',
            city: city || '',
            neighborhood: neighborhood || '',
            street: street || '',
            street_number: street_number || '',
            postal_code: postal_code || '',
            street_complement: street_complement || '',
            profile_image: profile_image || ''
        }
    },
    validationSchema: Yup.object().shape({
        name: Yup.string().required('Digite seu nome completo!'),
        date_birth: BrDate.required('Digite sua data de nascimento!'),
        nickname: Yup.string().required('Digite seu nickname!'),
        state: Yup.string().required('Digite seu estado de origem!'),
        city: Yup.string().required('Digite sua cidade!'),
        neighborhood: Yup.string().required('Digite seu bairro!'),
        street: Yup.string().required('Digite sua rua!'),
        street_number: Yup.string().required('Digite o número da sua casa!'),
        postal_code: Yup.string().length(9).required("Campo obrigatório")
    }),
    handleSubmit(values, { props, setValues }) {
        const options = {
            body: {
                user: {
                    name: values.name,
                    email: values.email,
                    date_birth: values.date_birth,
                    street: values.street,
                    street_number: values.street_number,
                    street_complement: values.street_complement,
                    neighborhood: values.neighborhood,
                    city: values.city,
                    state: values.state,
                    country: "Brasil",
                    postal_code: values.postal_code,
                    phone_number: values.phone_number,
                    cpf: values.cpf,
                    nickname: values.nickname,
                    profile_image: values.profile_image
                }
            }
        }
        API.put(config.APIS.PLAYERSTARS, '/player', options)
            .then(response => {
                console.log(":::::::OPTIONS:::::::", options)
                window.location.href = '/perfil'
            })
            .catch(error => {
                console.log(":::::::OPTIONS:::::::", options)
                window.alert('Erro fazendo PUT dos usuario!');
            })
    }
})(EditarPerfil));

export default SaveEdit
