import React, { useState } from 'react'
import { useSelector } from 'react-redux';
import imgStarBlue from "../Converter/img/star-blue.png";
import imgStarGold from "../Converter/img/star-gold.png";
import seta from "../Converter/img/seta.svg";
import './styles.scss';
import { ToastContainer, toast } from "react-toastify";

const Converter = () => {

    const [quantidade, setQuantidade] = useState(""); 
    const data = useSelector(state => state.user.data);
    const conversao = 1;

   function handleChange (e) {
        setQuantidade({ quantidade: e.target.value });
      };

     function converterPremio () {
        if ({quantidade} > data.golden_star_balance) {
          toast.error("Saldo insuficiente.");
          return;
        }
        if ({quantidade} === 0 || !quantidade) {
          toast.warn("Informe um valor para ser convertido.");
          return;
        }
      };

    return (
        <React.Fragment>

            <ToastContainer
          style={{ fontSize: "16px" }}
          autoClose={5000}
          hideProgressBar={false}
          newestOnTop={false}
          closeOnClick
          rtl={false}
          pauseOnVisibilityChange
          draggable
          pauseOnHover
        />

            <section className="containerConverter">       
                <div className="contentConverter">                   
                    <div className="infoConverter"> 
                        <h1>Converter Stars</h1>
                        <p className="saldo">Seu saldo de Star Gold é de: {data.golden_star_balance}</p>
                        <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. 
                        Nam vestibulum, mi vel placerat auctor, tellus sapien congue ex, id ornare mi sem sit amet ex.</p>
                    </div>
                    <div className="converterStars">
                        <img className="img-star" src={imgStarGold} alt="Stars Gold"></img>
                        <fieldset>
                            <input 
                            className="inputStar" 
                            onChange={handleChange.bind(this)}
                            value={quantidade}
                            type="number" 
                            required/>
                            <label className="label" htmlFor="StarsBlue">Stars Gold</label>
                        </fieldset>
                        <img className="seta" src={seta} alt="conversao"></img>
                        <fieldset>
                            <input 
                            className="inputStar" 
                            onChange={handleChange.bind(this)}
                            value={quantidade && quantidade * conversao}         
                            type="number" 
                            required/>
                            <label className="label" htmlFor="StarsBlue">Stars Blue</label>
                        </fieldset>
                        <img className="img-star" src={imgStarBlue} alt="Stars Blue"></img>
                    </div>
                        <button className="btnDefault"  onClick={converterPremio.bind(this)}>Converter</button>                              
                </div>
            </section>
        </React.Fragment>        
    )
}

export default Converter
