import React from "react";
import "../../../styles/base.scss";

const AlertTemplate = ({ message, close }) => {
  const _menssage = message;
  return (
    <>
      <div
        style={{
          display: "block",
          position: "fixed",
          center: "100%",
          background: "rgba(0, 0, 0, 0.8)",
          height: "100vh",
          width: "100vw",
          zIndex: "10",
        }}
      ></div>
      <div
        style={{
            display: "block",
            position: "fixed",
            transform: "translate(-50%, -50%)",
            width: "300px",
            borderRadius: "4px",
            backgroundColor: "#fff",
            textAlign: "center",
            textJustify: "center",
            padding: "20px",
            color: "#000",
            fontSize: "18px",
            zIndex: "10",
        }}
      >
        {_menssage}
      </div>
      <button onClick={close}>X</button>
    </>
  );
};

export default AlertTemplate;
